	Log.Info("Executing Javasrcipt");
	var queryForScenarioCount = 'Select ([Version].[Version Name].filter(#.Key <=0).count);';
	var getScenarioCount = queryModule.select(queryForScenarioCount);
	Log.Info("ScenarioCount" +  JSON.stringify(getScenarioCount));
	var ScenarioCount = getScenarioCount.Result;
	var queryForVersionCount = 'Select ([Version].[Version Name].filter(#.Key >=0).count);';
	var getVersionCount = queryModule.select(queryForVersionCount);
	Log.Info("VersionCount" +  JSON.stringify(getVersionCount));
	var VersionCount = getVersionCount.Result; 
	
	var ActionStartProcedure = 'exec procedure ActionStart {"User" : "'+User+'" , "RunID" : "'+ RunID +'","RunIDParent" : "'+ RunIDParent +'", "ActionType" : "'+ActionType+'", "Product" : "'+Product+'","ScenarioCount" : '+ScenarioCount+',"VersionCount" : '+VersionCount+',"ScenarioName" : "' +Version+'"};';
	var ExecuteActionStartProcedure = queryModule.select(ActionStartProcedure);
	Log.Info("SCS Command ... " + ExecuteActionStartProcedure);
	
	var queryForPotential1 = 'Select Version.[Version Name].[' + Version + '].Key;';
	var lsResponse1 = queryModule.select(queryForPotential1);
    Log.Info("Version Key ... " + lsResponse1);
    var SCPVersionKey = lsResponse1.Result;
	
	
	var queryForPotential2 = 'Select ([Version].[Version Name].[' + Version + '] * {(if (Measure.[D Inventory Planning Parameter] == 1) then "true" else "false") as Transient.[Param Value A],(if (Measure.[D Inventory Planning Parameter] == 2) then "true" else "false") as Transient.[Param Value B],(if (Measure.[D Inventory Planning Parameter] == 3) then "true" else "false") as Transient.[Param Value C],(if (Measure.[D Inventory Planning Parameter] == 4) then "true" else "false") as Transient.[Param Value D],(if (Measure.[D Solver Flag Inventory Plan policy] == "MIN_MAX") then "true" else "false") as Transient.[Param Value E],(if (Measure.[D Solver Flag Inventory Plan Type] == "BOS_TO_QUANTITY") then "true" else "false") as Transient.[Param Value F]});';
    var lsResponse2 = queryModule.select(queryForPotential2);
    Log.Info("SCSParameters ... " + lsResponse2);
	var dataCellSet = cellsetModule.createCellSet(lsResponse2);
		
	var SCPIndependentDemandOnly = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value A'));
	var SCPDependentDemandOnly = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value B'));
	var SCPIndependentAndDependentDemand = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value C'));
	var SCPAdvancedInventoryPlanning = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value D'));
	var SCPMIN_MAX = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value E'));
	var SCPSolverFlagInventoryPlanType = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('Param Value F'));
		
    var queryForExecution = 'exec procedure WeeklyBosToQuantityConversionMasterProcedure {"Version" : "' + Version + '", "IndependentDemandOnly" : ' + SCPIndependentDemandOnly + ', "DependentDemandOnly" : ' + SCPDependentDemandOnly + ', "IndependentAndDependentDemand" : ' + SCPIndependentAndDependentDemand + ', "AdvancedInventoryPlanning" : ' + SCPAdvancedInventoryPlanning + ', "MIN_MAX" : ' + SCPMIN_MAX + ',"InventoryPlanType" : ' + SCPSolverFlagInventoryPlanType + ',"VersionKey" : ' + SCPVersionKey + '};';
	Log.Info("WeeklyBosToQuantityConversionMasterProcedure ... " + queryForExecution);
	queryModule.update(queryForExecution);

	var queryforPostProcess = 'deleteversion("D Bos To Quantity Scenario");';
    Log.Info("deleting Version ... " + queryforPostProcess);
	queryModule.update(queryforPostProcess);
	
	
	
};


	return {
		DBosToQuantity:DBosToQuantity
	};
	});

