    var Version = parsedParams.Version;
    var queryModule = require('o9/data/query');
	var Rule = parsedParams.DMRule;
	var FromLoc = parsedParams.Activity1BODLaneFromLoc;
    var ToLoc = parsedParams.Activity1BODLaneToLoc;
	var Transmode = parsedParams.Activity2TransMode;
	var Prefix = parsedParams.PrefixFB;
	
	Log.Info('.......Starting with a Smile');

	//Populating Measure.[Activity1 From Location]
    var SetFromLoc = 'scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity1 From Location] = \"'+ FromLoc +'\" ; end scope;';
	Log.Info('Source Location is '+ FromLoc);
	queryModule.update(SetFromLoc);
	
	//Populating Measure.[Activity1 To Location]
	var SetToLoc = 'scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity1 To Location] = \"'+ ToLoc +'\" ; end scope;';
	Log.Info("Destination Locations are "+ ToLoc); 
    queryModule.update(SetToLoc);	

	//Populating Measure.[Activity2 Transmode]
	var SetTransmode = 'scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity2 Transmode] = \"'+ Transmode +'\" ; end scope;';
	Log.Info("Transmode used "+ Transmode); 
    queryModule.update(SetTransmode);

	var SplitToLoc = String(ToLoc).split(",");

	if (Prefix === null){
		var UsePF = "False";
		Log.Info('Prefix availability '+ UsePF);
	}
	else {
		UsePF = "True";
		Log.Info('Prefix availability '+ UsePF)
	}

	//Populating Concatinated String for Measure.[Activity1 BODLane]
	var concatelane = [];
	if (UsePF === "True"){
		for (var a = 0; a < SplitToLoc.length; a++){
		concatelane[a] = `${Prefix}_From_${FromLoc}_To_${SplitToLoc[a]}` ;
		var b = a+1;
		Log.Info('Lane '+ b +" is "+ concatelane[a]);
		}
	} else if (UsePF === "False"){
		for (var c = 0; c < SplitToLoc.length; c++){
			concatelane[c] = `Lane_From_${FromLoc}_To_${SplitToLoc[c]}` ;
			var d = c+1;
			Log.Info('Lane '+ d +" is "+ concatelane[c]);
			}
	}
	var Smile = concatelane[0];

	if (SplitToLoc.length > 1){
		var flag = "True";
		Log.Info('Lanes to be created are more than 1 '+ flag);
	}

	if (flag === "True"){
		for (var j = 0; j < SplitToLoc.length-1; j++){
			var IDB = `${Smile},${concatelane[j+1]}`;
