			var r = j+1;
			Log.Info('Check '+ r +" = "+ Smile);
		}
	}
	Log.Info('Activity1 BODLane '+ Smile);

	var CreateLane = `Scope: ([Version].[Version Name].[${Version}] * [Data Object].[Data Object].[Item BODLane Association] * [DM Rule].[Rule].[${Rule}]); Measure.[Activity1 BODLane] = \"${Smile}\"; end scope;`;
	Log.Info('Measure.[Activity1 BODLane] = \"' + Smile + "\"");
	queryModule.update(CreateLane);

	//Populating Activity1 Members 
	if (UsePF === "True"){
			for (var i = 0; i < SplitToLoc.length; i++){
			Log.Info('Creating a BODLane from ' + FromLoc + " To " + SplitToLoc[i]);
			
			var CreateActivity1 = `createmember([Activity1].[Activity1]={,\"${Prefix}_From_${FromLoc}_To_${SplitToLoc[i]}\"},[Activity1].[From Location]={,\"${FromLoc}\"},[Activity1].[To Location]={,\"${ToLoc[i]}\"},[Activity1].[Activity Type1]={,"Distribution"});` ;
			queryModule.update(CreateActivity1);

            var SetPrefix = `updatemember (Activity1.[Activity1]={,\"${Prefix}_From_${FromLoc}_To_${SplitToLoc[i]}\"}, Activity1.[Prefix]={\"${Prefix}\",});`;
            queryModule.update(SetPrefix);

            Log.Info('Prefix set as '+ Prefix);		
			var DCCount = i + 1;
		}
	} else if (UsePF === "False"){
		for (var k = 0; k < SplitToLoc.length; k++){
			Log.Info('Creating a BODLane from ' + FromLoc + " To " + SplitToLoc[k]);
			
			CreateActivity1 = `createmember([Activity1].[Activity1]={,\"Lane_From_${FromLoc}_To_${SplitToLoc[k]}\"},[Activity1].[From Location]={,\"${FromLoc}\"},[Activity1].[To Location]={,\"${ToLoc[k]}\"},[Activity1].[Activity Type1]={,"Distribution"});` ;
			queryModule.update(CreateActivity1);

            SetPrefix = `updatemember (Activity1.[Activity1]={,\"Lane_From_${FromLoc}_To_${SplitToLoc[k]}\"}, Activity1.[Prefix]={\"Lane\",});`;
            queryModule.update(SetPrefix);
		
            Log.Info('Prefix set as Lane');
			DCCount = k + 1;		
		}
	}

	Log.Info('Total Lanes created are ' + DCCount);
	
	Log.Info('..................Ending with a Cheesy Smile..................');

};
return {
	MultiDestinations:MultiDestinations
	};
});

