define('o9.SupplyPlan006.BulkScenarioMerge', ['o9/data/query', 'o9/data/cellset'], function () {
    var BulkScenarioMerge = function(o9Params){
	Log.Info("Started Java Script o9.SupplyPlan006.BulkScenarioMerge");
	var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	var ParsedParams = JSON.parse(o9Params);
	Log.Info('Passed Fields'+ JSON.stringify(ParsedParams));
	var SourceScenario = ParsedParams.Source;
	var TargetScenario = ParsedParams.Target;
	var Demand = ParsedParams.Demand ;
	var Transactional = ParsedParams.Transactional ;
	var CapacityMFG = ParsedParams.CapacityMFG ;
	var CapacityDIST = ParsedParams.CapacityDIST ;
	var CapacityPROC = ParsedParams.CapacityPROC ;
	var NetworkMFG = ParsedParams.NetworkMFG ;
	var NetworkDIST = ParsedParams.NetworkDIST ;
	var NetworkPROC = ParsedParams.NetworkPROC ;

	var Copy = function(M){
		var Grainquery = 'select (_SchemaPlan.[_Measure].['+M+'].relatedmembers([_MeasureGroup]).relatedmembers([_DimensionUsage]) ) on row, () on column include memberproperties {_SchemaPlan.[_DimensionUsage],[DimensionUsageDimensionName],[DimensionUsageDimensionAttribute]};';
			var Grains = queryModule.select(Grainquery);
			var query = 'Scope:([Version].[Version Name].['+TargetScenario+']';
			for(var k = 0; k < Grains.Meta[0].DimensionValues.length; k++){
				var Grain = Grains.Meta[0].DimensionValues[k];
				var memberProps = Grain.MemberProperties;
				var dim , attribute;
				for(var l = 0; l < memberProps.length ; l++){
					var obj = memberProps[l];
					if(obj.PropertyName === "DimensionUsageDimensionName"){
						dim = obj.PropertyValue; }
					if(obj.PropertyName === "DimensionUsageDimensionAttribute"){
						attribute = obj.PropertyValue; }
				}
				if(dim != "Version"){
					var string1 = ' * ['+dim+'].['+attribute+']';
					query = query.concat(string1);
				}
				 Log.Info("String: " + string1);
			}
			query = query.concat(");\n");
			query = query.concat('Measure.['+M+'] = Measure.['+M+']@(Version.[Version Name].['+SourceScenario+']);\nend scope;');
			queryModule.update(query);
	};
	
	var PassMeasurestoCopy = function(MG){
		var Measures = cellsetModule.createCellSet(MG);
		if(Measures.rowCount > 0){
			for (var i = 1; i < Measures.colCount ; i++){
			var M = Measures.row(0).cell(i);
			if(M !== null){
				Copy(M);
			}	
		}
		}
	};

var IdentifyMeasures = function(measureGroup, filter) {
    var Parametersquery = "SELECT _SchemaPlan.[_MeasureGroup].[" + measureGroup + "].Children(_SchemaPlan.[Plan-MeasureGroup-Measure]).filter(#.Name startswith [" + filter + "]);";
    var Parametermeasures = queryModule.select(Parametersquery);
    var measureNames = Parametermeasures.map(item => item.Name);
    var selectQuery = 'SELECT (&CWV) ON row, ({' + measureNames.map(name => 'Measure.[' + name + ']').join(', ') + '}) on column;';
    var Measures = queryModule.select(selectQuery);
    PassMeasurestoCopy(Measures);
};


	
	if (Demand === true) {
		IdentifyMeasures("009.002 Scenario Merge Demand", "Demand Parameter");
   }

  if (Transactional === true) {
		IdentifyMeasures("009.004 Scenario Mergre Transactional", "Transactional Parameter");
   }

  if (CapacityMFG === true) {
		IdentifyMeasures("009.001 Scenario Merge Capacity", "Capacity MFG Parameter");
  }

  if (CapacityDIST === true) {
		IdentifyMeasures("009.001 Scenario Merge Capacity", "Capacity DIST Parameter");
  }

   if (CapacityPROC === true) {
		IdentifyMeasures("009.001 Scenario Merge Capacity", "Capacity PROC Parameter");
  }

  if (NetworkMFG === true) {
		IdentifyMeasures("009.005 Scenario Merge Network", "Network MFG Parameter");
  }

  if (NetworkDIST === true) {
		IdentifyMeasures("009.005 Scenario Merge Network", "Network DIST Parameter");
  }

  if (NetworkPROC === true) {
		IdentifyMeasures("009.005 Scenario Merge Network", "Network PROC Parameter");
   }
   if (Inventory === true) {
		CopyMeasures("009.003 Scenario Merge Inventory", "Inventory PROC Parameter");
   }

};   
	
return {
	BulkScenarioMerge:BulkScenarioMerge
	};
});

