define('o9.referencemodelretailmfp.SeedLP', ['o9/data/query', 'o9/data/cellset'], function () {
	var SeedLP = function (o9Params) 	{
		Log.Info("------------------------------------------------------------Started executing plugin------------------------------------------------------------");

        var parsedParams = JSON.parse(o9Params);
		Log.Info("------------------------------------------------------------This is the value of the parsedParams------------------------------------------------------------");
		Log.Info(JSON.stringify(parsedParams));
		const queryMod = require('o9/data/query');
		
		var VersionFB = parsedParams.Version;
        var SelectFB = parsedParams.Select;
        var DepartmentFB = parsedParams.Department.join('", "');
        var ChannelFB = parsedParams.Channel.join('", "');
		var StoreFB = parsedParams.Store.join('", "');
		var SellingSeasonFB = parsedParams.SellingSeason.join('", "');
		var QuarterFB = parsedParams.Quarter.join('", "');

        var itemQuery='spread cartesian scope:(&DynamicScenarios.Filter( #.Name == "'+VersionFB+'") * &RE2EDepartments.Filter(#.Name in {"'+DepartmentFB+'"}) * &RE2ELocations.Filter(#.Name in {"'+StoreFB+'"}).ancestorsAtLevel([Location Country]) * Time.[Planning Quarter].Filter(#.Name in {"'+QuarterFB+'"}).RelatedMembers([Planning Month]) * &RE2EChannels.Filter(#.Name in {"'+ChannelFB+'"}) * &MFPSellingSeason.Filter(#.Name in {"'+SellingSeasonFB+'"})); Measure.[LP SelectSeedOption] = if (Measure.[Class Selling Season Week Association]  == 1) then 1; end scope;';
        
        Log.Info("--------------------Intersection creation for seeding--------------------");
        Log.Info(itemQuery);
        queryMod.update(itemQuery);
        
        if(SelectFB == 1){
            itemQuery='exec procedure LYSeedingLPPlan { "Version" : "'+VersionFB+'" , "Department" : ["'+DepartmentFB+'"] , "Store" : ["'+StoreFB+'"] , "Quarter" : ["'+QuarterFB+'"] , "Channel" : ["'+ChannelFB+'"] , "SellingSeason" : ["'+SellingSeasonFB+'"]};';
            Log.Info("--------------------Seeding Location Plan from LY Values--------------------");
            Log.Info(itemQuery);
            queryMod.update(itemQuery);
        }
        else if(SelectFB == 2){
            itemQuery='exec procedure LLYSeedingLPPlan { "Version" : "'+VersionFB+'" , "Department" : ["'+DepartmentFB+'"] , "Store" : ["'+StoreFB+'"] , "Quarter" : ["'+QuarterFB+'"] , "Channel" : ["'+ChannelFB+'"] , "SellingSeason" : ["'+SellingSeasonFB+'"]};';
            Log.Info("--------------------Seeding Location Plan from LLY Values--------------------");
            Log.Info(itemQuery);
            queryMod.update(itemQuery); 
        }
        else if(SelectFB == 3){
            itemQuery='exec procedure LLLYSeedingLPPlan { "Version" : "'+VersionFB+'" , "Department" : ["'+DepartmentFB+'"] , "Store" : ["'+StoreFB+'"] , "Quarter" : ["'+QuarterFB+'"] , "Channel" : ["'+ChannelFB+'"] , "SellingSeason" : ["'+SellingSeasonFB+'"]};';
            Log.Info("--------------------Seeding Location Plan from LLLY Values--------------------");
            Log.Info(itemQuery);
            queryMod.update(itemQuery);
