define('o9.referencemodelretailmfp.VTDSSValidation', ['o9/data/query', 'o9/data/cellset'], function () {
    var VTDSSValidation = function (o9Params) {
        Log.Info("------------------------------------------------------------Started executing plugin------------------------------------------------------------");

        var parsedParams = JSON.parse(o9Params);
        Log.Info("------------------------------------------------------------This is the value of the parsedParams------------------------------------------------------------");
        Log.Info(JSON.stringify(parsedParams));
        const queryMod = require('o9/data/query');

        var C1SellingSeason = parsedParams.C1SellingSeason;
        var C2SellingSeason = parsedParams.C2SellingSeason;
        /*****/
      // Parsing dates for comparison
        var C1StartDate = new Date(parsedParams.C1StartDate);
        var C1EndDate = new Date(parsedParams.C1EndDate);
        var C2StartDate = new Date(parsedParams.C2StartDate);
        var C2EndDate = new Date(parsedParams.C2EndDate);
        var LastWeek = new Date(parsedParams.LastWeek);
        /*****/

        if ((C1SellingSeason === null || C1SellingSeason.length === 0) !== 
            (C2SellingSeason === null || C2SellingSeason.length === 0)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'C1SellingSeason and C2SellingSeason must either both be empty or both have values.'
            };
            Log.Info("Error Condition Met: " + JSON.stringify(RuleOutputToUI));
            Log.Error('Validation Error: Last Period Selling Season and Current Period Selling Season must either both be empty or both have values.');
            return; // Exit the function and return the error object
        } else {
            Log.Info("Validation Passed: Both Selling Seasons are correctly set.");
        }

        // Validate C1EndDate and C2EndDate against LastWeek
        if (C1EndDate > LastWeek || C2EndDate > LastWeek) {
            // Throwing an error message to the user
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'C1EndDate and C2EndDate must not be later than LastWeek'
            };
            // Logging the error
            Log.Info("Error Condition Met: " + JSON.stringify(RuleOutputToUI));
            Log.Error('Validation Error: Last Period and Current Period must not be later than the Last Week.');
            return;
        } else {
            Log.Info("Validation Passed: Dates are set correctly");
        }
        
        return {
            Status: 'Success',
            Message: 'Validation passed without errors.'
        }; 
    };
    
    return {
        VTDSSValidation: VTDSSValidation
    };
});

