define('o9.referencemodel.DeleteObsoleteMembersDeletion', ['o9/data/query', 'o9/data/cellset'], function () {
    var DeleteObsoleteMembersDeletion = function(o9Params){
	var queryModule = require('o9/data/query');
	var parsedParams = JSON.parse(o9Params);
	var Version = parsedParams.Version;
	var RunID = parsedParams.RunID;
	var ActionType = parsedParams.ActionType;
	var Product = parsedParams.Product;
	var User = parsedParams.User;
	var RunIDParent = parsedParams.RunIDParent;
	var cellsetModule = require('o9/data/cellset');
	Log.Info('Passed Fields'+ JSON.stringify(parsedParams));

	Log.Info("Reading Framework data for Delete Obsolete Memeber");
	var FrameworkDataRead = 'Select ([DM Rule].[Rule] * &CWV ) on row,({Measure.[Dimension Attribute Object], Measure.[Output Measure Name],Measure.[Order of Deletion], Measure.[Behavior Identifier], Measure.[Cascade Parent Deletion Dimension Attribute Object], Measure.[Delete Obsolete Members Deletion Limit], Measure.[Attribute Name Object], Measure.[Dimension Name Object]}) on column where {Measure.[Member Deletion Flag] == true} orderby Measure.[Order of Deletion];';
	var FrameworkData = queryModule.select(FrameworkDataRead);
	
	var FrameworkMeasure = cellsetModule.createCellSet(FrameworkData);
	
	for (var rule = 0; rule < FrameworkMeasure.rowCount ;rule++)
	{
		var DimensionAttributeObject = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Dimension Attribute Object'));
		var RuleIdentifier = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Behavior Identifier'));
		var DeleteMeasureName = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Output Measure Name'));
		var OrderOfDeletion = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Order of Deletion'));
		var ParentAttributeMember = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Cascade Parent Deletion Dimension Attribute Object'));
		var DeleteMemberLimit = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Delete Obsolete Members Deletion Limit'));
		var DimensionObject = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Dimension Name Object'));
		var AttributeObject = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Attribute Name Object'));

		
		if (RuleIdentifier	== 'Parent Member Cascade')
		{
				var DeleteMemberListSelectQuery = 'Select (&CWV * ['+ DimensionObject +'].['+ AttributeObject +'] ) on row, ({Measure.[' + DeleteMeasureName + ' Final]}) on column.filter(Measure.[' + DeleteMeasureName + ' Final] >0);';				
				var DeleteMemberListQuery = queryModule.select(DeleteMemberListSelectQuery);
				var DeleteMemberList = cellsetModule.createCellSet(DeleteMemberListQuery);
				
				FirstMemberToBeDeleted = DeleteMemberList.row(0).cell(DeleteMemberList.memberColumnIndex(DimensionObject,AttributeObject)).Name;
				
				// Command to create the member
				
				var CreatememberCommand = 'Createmember (['+ DimensionObject +'].[' + ParentAttributeMember + '] = {,[Ref Model Delete Obsolete Member]}, ['+ DimensionObject +'].['+ AttributeObject +'] = {,[' + FirstMemberToBeDeleted + ']});';
				
				Log.Info("CreatememberCommand ..." + CreatememberCommand);
				queryModule.update(CreatememberCommand)
				
				
				
				for (var i=0; i < DeleteMemberList.rowCount ;i++)
				{
						var MembertoBeUpdated = DeleteMemberList.row(i).cell(DeleteMemberList.memberColumnIndex(DimensionObject,AttributeObject)).Name;
						Log.Info("MembertoBeUpdated ..." + MembertoBeUpdated);
						
						var updatemember = 'Updatemember (['+ DimensionObject +'].['+ AttributeObject +'] = {,[' + MembertoBeUpdated + ']}, ' + ParentAttributeMember + ' = {,[Ref Model Delete Obsolete Member]});';
						
						Log.Info("updatemember ..." + updatemember);
						
						queryModule.update(updatemember)
						
				}
				
				var DeleteMember = 'Deletemember (['+ DimensionObject +'].['+ AttributeObject +'] = {,[Ref Model Delete Obsolete Member]}) Cascade;';
				Log.Info("DeleteMember ..." + DeleteMember);
				
				queryModule.update(DeleteMember)
		}
		
		if (RuleIdentifier == 'Individual Delete Member')
		{
				var QueryForBooleanMeasure = 'scope:(&CWV * ['+ DimensionObject +'].['+ AttributeObject +']); Measure.[' + DeleteMeasureName + ' Boolean] = if (Measure.[' + DeleteMeasureName + ' Final]>0) then TRUE; end scope;';
				queryModule.update(QueryForBooleanMeasure)
				
				var BulkDeleteMemberQuery = 'CreateMember (['+ DimensionObject +'].['+ AttributeObject +'] = {, ['+ DimensionObject +'].#.Name}, ['+ DimensionObject +'].['+ AttributeObject +'$InActive] = {Measure.[' + DeleteMeasureName + ' Boolean],}) using scope (Version.[Version Name].[CurrentWorkingView] * ['+ DimensionObject +'].['+ AttributeObject +']) where {Measure.[' + DeleteMeasureName + ' Boolean] == TRUE};';
				
				Log.Info("BulkDeleteMemberQuery ..." + BulkDeleteMemberQuery);
				
				queryModule.update(BulkDeleteMemberQuery)
				
				
		}
	
	}
	
	for (var rule = 0; rule < FrameworkMeasure.rowCount ;rule++)
	{
		var DimensionObject = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Dimension Name Object'));
		var AttributeObject = FrameworkMeasure.row(rule).cell(FrameworkMeasure.measureColumnIndex('Attribute Name Object'));
		var PurgeQuery = 'purge members([' + DimensionObject + '].[' + AttributeObject + ']);'
		Log.Info("Purge Query ..." + PurgeQuery)
		
		queryModule.update(PurgeQuery)
	}
	
	
};


	return {
		DeleteObsoleteMembersDeletion:DeleteObsoleteMembersDeletion
	};
	});

