define('o9.referencemodel.UpdateScenario', ['o9/data/query', 'o9/data/cellset'], function () {
    var UpdateScenario = function(o9Params){
      var queryModule = require('o9/data/query');
      var cellsetModule = require('o9/data/cellset');
      
        //updating global scenario count
        var FetchUpdatedVersionData = 'Select (Version.[Version Name].filter(#.Key < 0).filter(#.[Is Visible] == true).count);';
        var ScCount = queryModule.select(FetchUpdatedVersionData);
        var ScCountData = ScCount.Result;
        var GScCount = 'Scope:(Version.[Version Name].[CurrentWorkingView]);Measure.[Global Scenario Count] = '+ScCountData+';end Scope;';
        queryModule.update(GScCount);
        Log.Info("Updating Global Count" +GScCount);
  
        const userFreq = {};
        //pre processing
        var queryPreProcess = 'Scope: (&CWV * [Personnel].[Email]);Measure.[Scenario Count Per User] = 0;end scope;';
        queryModule.update(queryPreProcess);
        Log.Info("Pre processing.");
            
        //fetching all versions
        var versionQuery = 'Select ([Version].[Version Name].filter(#.Key < 0).filter(#.[Is Visible] == true));';
        var versionArr = cellsetModule.createCellSet(queryModule.select(versionQuery));
        //iterating through each version 
        for(var it = 0; it < versionArr.length; it++) {
            var x = versionArr.row(it).cell(versionArr.memberColumnIndex('Version Name'));
            var data1 = 'Select ([Version].[Version Name]['+x+'].filter(#.Key < 0).filter(#.[Is Visible] == true) * {Measure.[Scenario Created By]});';
            const data2 = cellsetModule.createCellSet(queryModule.select(data1));
            for (var t = 0; t < data2.rowCount; t++) {
            var emailInitial = data2.row(t).cell(data2.measureColumnIndex('Scenario Created By'));
            userFreq[emailInitial] = 0;
            //for eg: scenario count per user of tejaswini.akella@o9solutions.com is set as 0
        }
        //assigning email to its respective frequency
            var email = data2.row(0).cell(data2.measureColumnIndex('Scenario Created By'));
            if(userFreq[email] !== undefined) userFreq[email]++;
            Log.Info(`Assigned email: ${email} to frequency: ${frequency}`);
        }
        //updating the scenario count per user in each version
            for (let email in userFreq) {
                var query = 'Scope: (&CWV * [Personnel].[Email].['+email+']);Measure.[Scenario Count Per User] = '+userFreq[email]+';end scope;';
                queryModule.update(query);
                Log.Info("Updating Scenario Count Per User in version: ");
        }
  };   
  return {
      UpdateScenario:UpdateScenario
      };
  });

