define('o9.referencemodel.ProcessExecution', ['o9/data/query', 'o9/data/cellset'], function () {
	
    var ProcessExecution = function(o9Params)
	{
	var queryModule = require('o9/data/query');
	var parsedParams = JSON.parse(o9Params);
	//var Version = parsedParams.Version;
	//var RunID = parsedParams.RunID;
	//var ActionType = parsedParams.ActionType;
    //var Product = parsedParams.Product;
    //var User = parsedParams.User;
	//var RunIDParent = parsedParams.RunIDParent;
	var DataObject = parsedParams.DataObject;
	var Version = parsedParams.Version;
	var cellsetModule = require('o9/data/cellset');
	Log.Info('Passed Fields'+ JSON.stringify(parsedParams));
	
	// function for creating scenarios
	var ScenarioCreation = function(InputDataCellSet, i)
	{
		Log.Info("Started Scenario Creation computation");
		var SourceScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Source Scenario Name'));
		var TargetScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Target Scenario Name'));
		var ScenarioScope = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Scenario Scope'));
		
		//Steps to find the key of the source scenario
		var SourceScenarioKeyQuery = 'select Version.[Version Name].['+SourceScenario+'].Key;';
		var SourceKeyResponse = queryModule.select(SourceScenarioKeyQuery);
		var SourceScenarioKey = SourceKeyResponse.Result;
		var QueryForScenarioCreation;
		Log.Info("Scenario Scope..." + ScenarioScope);
		if (ScenarioScope == 'Weekly')
		{
			Log.Info("Weekly Scenario Creation Started");
			QueryForScenarioCreation = 'exec procedure WeeklyScopedScenario {"SeedVersionKey" : '+ SourceScenarioKey +', "ScenarioName" : "'+ TargetScenario +'"};';
			Log.Info("Scenario Creation Query ... " + QueryForScenarioCreation);
			queryModule.update(QueryForScenarioCreation);
		}
		
		if (ScenarioScope == 'Global')
		{
			Log.Info("Global Scenario Creation Started");
			QueryForScenarioCreation = 'CreateScenario('+ SourceScenarioKey +', 0, "'+ TargetScenario +'");';
			queryModule.update(QueryForScenarioCreation);
			Log.Info("Scenario Creation Query ... " + QueryForScenarioCreation);
		}
		
		if (ScenarioScope == 'Daily')
		{
			Log.Info("Daily Scenario Creation Started");
			QueryForScenarioCreation = 'exec procedure DailyScopedScenario {"SeedVersionKey" : '+ SourceScenarioKey +', "ScenarioName" : "'+ TargetScenario +'"};';
			Log.Info("Scenario Creation Query ... " + QueryForScenarioCreation);
			queryModule.update(QueryForScenarioCreation);
		}
		
		if (ScenarioScope == 'Monthly')
		{
			Log.Info("Monthly Scenario Creation Started");
			QueryForScenarioCreation = 'exec procedure MonthlyScopedScenario {"SeedVersionKey" : '+ SourceScenarioKey +', "ScenarioName" : "'+ TargetScenario +'"};';
			Log.Info("Scenario Creation Query ... " + QueryForScenarioCreation);
			queryModule.update(QueryForScenarioCreation);
		}
		
		Log.Info("Finished Scenario Creation computation");
	
	};
	
	// Function for calling data transformation (process interaction framework)
	var DataTransformation = function(InputDataCellSet, i)
	{
		var PluginQuery = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Query Input'));
		queryModule.update(PluginQuery);
	};
	
	//Function to run solver instance
	var SolverRun = function(InputDataCellSet, i)
	{
		Log.Info("Started Solver Run computation");
		var SolverName = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Solver Name'));
		var SourceScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Source Scenario Name'));
		var TargetScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Target Scenario Name'));
		
		if(SolverName == 'DailySCS'){
			RunDailySCS(SourceScenario);
		}
		if(SolverName == 'DailyLP'){
			RunDailyLP(SourceScenario);
		}
		if(SolverName == 'WeeklySCS'){
			RunWeeklySCS(SourceScenario);
		}
		if(SolverName == 'WeeklyLP'){
			RunWeeklyLP(SourceScenario);
		}
		if(SolverName == 'MonthlySCS'){
			RunMonthlySCS(SourceScenario);
		}
		if(SolverName == 'MonthlyLP'){
			RunMonthlyLP(SourceScenario);
		}
		if(SolverName == 'DailySCSDeployment'){
			RunDailyDeploymentSCS(SourceScenario);
		}

	};
	
	var RunDailySCS = function(Version)
	{
	
	var queryForPotential4 = 'Select ([Version].[Version Name].[' + Version + '] * {Measure.[D Solver Demand Slice Factor],Measure.[D Solver Flag Allow NOH],Measure.[D Solver Flag Respect Freeze Window For WIPs],Measure.[D Solver Flag Minimize Inventory Build Ahead],(if    (Measure.[D Solver Flag Inventory Plan Type] == "BOS_TO_QUANTITY") then "QUANTITY" else Measure.[D Solver Flag Inventory Plan Type]) as Transient.[D Solver Flag Inventory Plan Type Modified],Measure.[D Solver Flag Inventory Plan Policy],Measure.[D Solver Inventory Plan Bands],Measure.[D Material Constrained],Measure.[D Capacity Constrained],Measure.[D Storage Constrained],Measure.[D Solver Capacity Bands To Minimize Lateness],Measure.[D Solver Delete Data],Measure.[D Solver Run Type Flag],Measure.[D Solver SS Build Ahead Limit],Measure.[D Solver Flag Minimize No Carry Violations],Measure.[D Solver Flag Try Material Reallocation In RCA],Measure.[D Solver Flag Inventory Plan Strategy],Measure.[D Solver Flag No Peggings],Measure.[D Solver Flag SupplyCode Plan],Measure.[D Solver Flag Shelf Life Plan],Measure.[D Plan Date Hard Peg],Measure.[D Min Time Hard Peg],Measure.[D Late Plan Strategy],Measure.[D Solver Demand Slice Threshold], Measure.[D Plan Start Bucket],Measure.[D Plan End Bucket],Measure.[D Plan Current Bucket],Measure.[Deployment Plan End Bucket],Measure.[D Global Demand Build Late Limit]});';
		var lsResponse4 = queryModule.select(queryForPotential4);
		Log.Info("SCSParameters ... " + lsResponse4);
		var dataCellSet = cellsetModule.createCellSet(lsResponse4);
		
		
		var GlobalBuildLateLimit = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Global Demand Build Late Limit'));
		var DailySolverStartBucket = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Plan Start Bucket'));
		var DailySolverCurrentBucket = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Plan Current Bucket'));
		var DailySolverEndBucket = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Plan End Bucket'));
		var SCPSolverDemandSliceFactor = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Demand Slice Factor'));
		var SCPSolverFlagAllowNOH = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Allow NOH'));
		var SCPSolverFlagRespectFreezeWindowForWIPs = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Respect Freeze Window For WIPs'));
		var SCPSolverFlagMinimizeInventoryBuildAhead = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Minimize Inventory Build Ahead'));
		var SCPSolverFlagInventoryPlanType = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Inventory Plan Type Modified'));
		var SCPSolverFlagInventoryPlanPolicy = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Inventory Plan Policy'));
		var SCPSolverInventoryPlanBands = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Inventory Plan Bands'));
		var SCPMaterialConstrained = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Material Constrained'));
		var SCPCapacityConstrained = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Capacity Constrained'));
		var SCPStorageConstrained = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Storage Constrained'));
		var SCPSolverCapacityBandsToMinimizeLateness = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Capacity Bands To Minimize Lateness'));
		var SCPSolverDeleteData = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Delete Data'));
		var SCPSolverRunTypeFlag = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Run Type Flag'));
		var SCPSolverSSBuildAheadLimit = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver SS Build Ahead Limit'));
		var SCPSolverFlagMinimizeNoCarryViolations = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Minimize No Carry Violations'));
		var SCPSolverFlagTryMaterialReallocationInRCA = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Try Material Reallocation In RCA'));
		var SCPSolverFlagInventoryPlanStrategy = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Inventory Plan Strategy'));
	
		var SCPSolverDemandSliceThreshold = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Demand Slice Threshold'));
		var SCPLatePlanStratergy = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Late Plan Strategy'));
		var SCPMinTimeHardPeg = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Min Time Hard Peg'));
		var SCPPlanDateHardPeg = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Plan Date Hard Peg'));
		var SCPShelfLife = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag Shelf Life Plan'));
		var SCPSupplyCode = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag SupplyCode Plan'));
		var SCPSolverNoPegging = dataCellSet.row(0).cell(dataCellSet.measureColumnIndex('D Solver Flag No Peggings'));
	
		var queryForExecution = 'exec procedure DailyRunBasicPlan {"Version" : ["' + Version + '"], "DSolverCurrentBucket" : "' + DailySolverCurrentBucket + '" , "DSolverStartingBucket" : "' + DailySolverStartBucket + '", "DSolverEndingBucket" : "' + DailySolverEndBucket + '","DSolverDemandSliceFactor" : ' + SCPSolverDemandSliceFactor + ',"DSolverFlagAllowNOH" : "' + SCPSolverFlagAllowNOH + '","DSolverFlagRespectFreezeWindowForWIPs" : "' + SCPSolverFlagRespectFreezeWindowForWIPs  + '","DSolverFlagMinimizeInventoryBuildAhead" : "' + SCPSolverFlagMinimizeInventoryBuildAhead + '","DSolverFlagInventoryPlanType" : "' + SCPSolverFlagInventoryPlanType +'","DSolverFlagInventoryPlanPolicy" : "' + SCPSolverFlagInventoryPlanPolicy + '","DSolverInventoryPlanBands" : "' + SCPSolverInventoryPlanBands + '","DMaterialConstrained" : "' + SCPMaterialConstrained + '","DCapacityConstrained" : "' + SCPCapacityConstrained +'","DStorageConstrained" : "' + SCPStorageConstrained +'" ,"DSolverDeleteData" : "' + SCPSolverDeleteData + '","DSolverCapacityBandsToMinimizeLateness" : "' + SCPSolverCapacityBandsToMinimizeLateness + '","DSolverSSBuildAheadLimit" : ' + SCPSolverSSBuildAheadLimit + ',"DSolverFlagMinimizeNoCarryViolations" : "' + SCPSolverFlagMinimizeNoCarryViolations +'","DSolverFlagTryMaterialReallocationInRCA" : "' + SCPSolverFlagTryMaterialReallocationInRCA + '","DSolverFlagInventoryPlanStrategy" : "' + SCPSolverFlagInventoryPlanStrategy + '","DDemandSliceThreshold" : ' + SCPSolverDemandSliceThreshold + ',"DLatePlanStrategy" : "' + SCPLatePlanStratergy + '","DMinTimeHardPeg" : "' + SCPMinTimeHardPeg + '","DPlanDateHardPeg" : "' + SCPPlanDateHardPeg + '","DShelfLifePlan" : "' + SCPShelfLife + '","DSupplyCodePlan" : "' + SCPSupplyCode + '","DNoPeggings" : "' + SCPSolverNoPegging + '"};';
		Log.Info("SCS Command ... " + queryForExecution);
		queryModule.update(queryForExecution);
		
	};
	
