	{
		Log.Info("Started Scenario Creation computation");
		var SourceScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Source Scenario Name'));
		var TargetScenario = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Target Scenario Name'));
		var ScenarioScope = InputDataCellSet.row(i).cell(InputDataCellSet.measureColumnIndex('Process Execution Scenario Scope'));
		
		//Steps to find the key of the source scenario
		var SourceScenarioKeyQuery = 'select Version.[Version Name].['+SourceScenario+'].Key;';
		var SourceKeyResponse = queryModule.select(SourceScenarioKeyQuery);
		var SourceScenarioKey = SourceKeyResponse.Result;
		var QueryForScenarioCreation;
		Log.Info("Scenario Scope..." + ScenarioScope);
		if (ScenarioScope == 'Weekly')
		{
			Log.Info("Weekly Scenario Creation Started");
			QueryForScenarioCreation = 'exec procedure WeeklyScopedScenario {"SeedVersionKey" : '+ SourceScenarioKey +', "ScenarioName" : "'+ TargetScenario +'"};';
			Log.Info("Scenario Creation Query ... " + QueryForScenarioCreation);
			queryModule.update(QueryForScenarioCreation);
		}
		
		if (ScenarioScope == 'Global')
