var parsedParams = JSON.parse(o9Params);
var queryModule = require('o9/data/query');
var cellsetModule = require('o9/data/cellset');
var creationdate = parsedParams.CreationDate;	
var Version = parsedParams.Version;

for(var i = 0; i < Version.length; i++){
	var x = Version[i];
	var basescenariokey ='SELECT Version.[Version Name].['+ x +'].Key;';
	var lsResponse = queryModule.select(basescenariokey);
	Log.Info("lsResponse is "+ lsResponse);
	var Key = lsResponse.Result;
	Log.Info("Key is "+ Key);
	var scenariotoversion = 'createversion('+Key+', "Archive - '+x+'", false, false);';
    Log.Info("version created- "+ scenariotoversion);
    queryModule.update(scenariotoversion);
    var sol = 'Select ([Version].[Version Name].['+x+'] ) on row, ({Measure.[Product]}) on column;';
	var VersionData = queryModule.select(sol);
	var Scenariodata = cellsetModule.createCellSet(VersionData);
	var str2 = Scenariodata.row(0).cell(Scenariodata.measureColumnIndex('Product'));
	if(str2 == "Master Planning"){
		var thres = 'select (&CWV * {coalesce(Measure.[MP Version Age Threshold],Measure.[Scenario Age Threshold]) as Transient.Threshold});';
		var hold = queryModule.select(thres);
		var th = cellsetModule.createCellSet(hold);
		var threshold = th.row(0).cell(th.measureColumnIndex('Threshold'));
		Log.Info("MP Threshold updated by user is " + threshold);
		var date =  queryModule.select('select dateadd (todatetime(\"'+ creationdate +'\"), '+threshold+'  ,[day]);').Result;
		var expirydate = 'updatemember ([Version].[Version Name] = {,"Archive - '+x+'"},[Version].[Version Expiry Date] = {todatetime(\"'+ date +'\"),});';
		queryModule.update(expirydate);
	}
	else if(str2 == "Demand Planning"){
		var thres = 'select (&CWV * {coalesce(Measure.[DP Version Age Threshold],Measure.[Scenario Age Threshold]) as Transient.Threshold});';
		var hold = queryModule.select(thres);
		var th = cellsetModule.createCellSet(hold);
		var threshold = th.row(0).cell(th.measureColumnIndex('Threshold'));
		Log.Info("DP Threshold updated by user is " + threshold);
		var date =  queryModule.select('select dateadd (todatetime(\"'+ creationdate +'\"), '+threshold+'  ,[day]);').Result;
		var expirydate = 'updatemember ([Version].[Version Name] = {,"Archive - '+x+'"},[Version].[Version Expiry Date] = {todatetime(\"'+ date +'\"),});';
		queryModule.update(expirydate);
		}
	else if(str2 == "Daily Planning"){
		var thres = 'select (&CWV * {coalesce(Measure.[CT Version Age Threshold],Measure.[Scenario Age Threshold]) as Transient.Threshold});';
		var hold = queryModule.select(thres);
		var th = cellsetModule.createCellSet(hold);
		var threshold = th.row(0).cell(th.measureColumnIndex('Threshold'));
		Log.Info("CT Threshold updated by user is " + threshold);
		var date =  queryModule.select('select dateadd (todatetime(\"'+ creationdate +'\"), '+threshold+'  ,[day]);').Result;
		var expirydate = 'updatemember ([Version].[Version Name] = {,"Archive - '+x+'"},[Version].[Version Expiry Date] = {todatetime(\"'+ date +'\"),});';
		queryModule.update(expirydate);
		}
	else if(str2 == "MEIO"){
		var thres = 'select (&CWV * {coalesce(Measure.[MEIO Version Age Threshold],Measure.[Scenario Age Threshold]) as Transient.Threshold});';
		var hold = queryModule.select(thres);
		var th = cellsetModule.createCellSet(hold);
		var threshold = th.row(0).cell(th.measureColumnIndex('Threshold'));
		Log.Info("MEIO Threshold updated by user is " + threshold);
		var date =  queryModule.select('select dateadd (todatetime(\"'+ creationdate +'\"), '+threshold+'  ,[day]);').Result;
		var expirydate = 'updatemember ([Version].[Version Name] = {,"Archive - '+x+'"},[Version].[Version Expiry Date] = {todatetime(\"'+ date +'\"),});';
		queryModule.update(expirydate);
		}
	else {
		var thres = 'Select ([Version].[Version Name].[CurrentWorkingView] ) on row, ({Measure.[Scenario Age Threshold]}) on column;';
		var hold = queryModule.select(thres);
		var th = cellsetModule.createCellSet(hold);
		var threshold = th.row(0).cell(th.measureColumnIndex('Scenario Age Threshold'));
		Log.Info("Global Threshold updated by user is " + threshold);
		var date =  queryModule.select('select dateadd (todatetime(\"'+ creationdate +'\"), '+threshold+'  ,[day]);').Result;
		var expirydate = 'updatemember ([Version].[Version Name] = {,"Archive - '+x+'"},[Version].[Version Expiry Date] = {todatetime(\"'+ date +'\"),});';
		queryModule.update(expirydate);
		}
	var deletearchivedscenario = queryModule.update('updatemember ([Version].[Version Name] = {,"'+x+'"},[Version].[Version Expiry Date] = {dateadd (todatetime(\"'+ creationdate +'\"), '+(-1)+'  ,[day]),});');
	Log.Info("deletearchivedscenario "+deletearchivedscenario);
}
};  


return {
Archive:Archive
};
});

