define('o9.referencesupplyplanning.BulkMemberCreate', ['o9/data/query', 'o9/data/cellset'], function () {
    var BulkMemberCreate = function(o9Params){
	Log.Info("Started Java Script o9.referencesupplyplanning.BulkMemberCreate");
	var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	var ParsedParams = JSON.parse(o9Params);
	var DataObjectPassed = ParsedParams.DataObject;
	Log.Info('Passed Fields'+ JSON.stringify(ParsedParams));
	var Version = ParsedParams.Version;


    var Create_member = function(DataObject, DimensionName) {
	
		Log.Info("CreatememberQuery Function Started");
		var CreateMemberQuery = 'CreateMember(';
		
		var MemberSelectQuery = 'Select ([GraphCubeDim_Sequence].[GraphCubeAttr_Sequence] * [Data Object].[Data Object].['+DataObject+'] * [Version].[Version Name].['+Version+'] ) on row, ({Measure.[Member Creation Input Attribute], Measure.[Member Creation Input Attribute Member Rule], Measure.[Member Creation Input Is Property], Measure.[Member Creation Input Member Key]}) on column;';
		Log.Info(MemberSelectQuery);
		var MemberQuery = queryModule.select(MemberSelectQuery);
		var MemberQueryDataCellSet = cellsetModule.createCellSet(MemberQuery);
		var string;
		var string2;
		
		for (var i=0; i< MemberQueryDataCellSet.rowCount ; i++)
		{
			var AttributeName = MemberQueryDataCellSet.row(i).cell(3);
			var ValueName = MemberQueryDataCellSet.row(i).cell(4);
			var IsProperty = MemberQueryDataCellSet.row(i).cell(5);
			var KeyMeasure = MemberQueryDataCellSet.row(i).cell(6);

			//Log.Info("Attribute Name" +AttributeName + "Value Name" +ValueName +"IsProperty" +IsProperty +"KeyMeasure"+KeyMeasure );
		
			if (IsProperty == true){
				string = '['+ DimensionName +'].['+AttributeName+'] = {'+ ValueName +',}';
			} else if (KeyMeasure !== null){
				string = '['+ DimensionName +'].['+AttributeName+'] = {'+ KeyMeasure +', '+ ValueName +'}';
			} else {
				string = '['+ DimensionName +'].['+AttributeName+'] = {,'+ ValueName +'}';
			}
		
			CreateMemberQuery = CreateMemberQuery.concat(string);
		
			if (i != (MemberQueryDataCellSet.rowCount - 1)) {
				string2 = ','	;
			} else {
				string2 = ') using scope ('
			}
			CreateMemberQuery = CreateMemberQuery.concat(string2);
			
			//Log.info(CreateMemberQuery);
		}
		// function call to identify the granularity of the measure specified in the sequence ID 0
		MeasureForGranularity = MemberQueryDataCellSet.row(0).cell(4);
		
		var MeasureName = MeasureForGranularity.substring(
			MeasureForGranularity.indexOf("[") + 1, 
			MeasureForGranularity.indexOf("]")
		);
		//Version = MemberQueryDataCellSet.row(1).cell(2);
		var ScopeString = MeasureGranularity(MeasureName);
		
		CreateMemberQuery = CreateMemberQuery.concat(ScopeString);
		
		Log.Info("CreatememberQuery generated");
		Log.Info(CreateMemberQuery);
		
		queryModule.update(CreateMemberQuery);
	};
	
	var MeasureGranularity = function(Measure) {
	
		Log.Info('Granularity Identification Started');
		var Grainquery = 'select (_SchemaPlan.[_Measure].['+Measure+'].relatedmembers([_MeasureGroup]).relatedmembers([_DimensionUsage]) ) on row, () on column include memberproperties {_SchemaPlan.[_DimensionUsage],[DimensionUsageDimensionName],[DimensionUsageDimensionAttribute]};';
		var Grains = queryModule.select(Grainquery);
			var query = '[Version].[Version Name].['+Version+']';
			for(var k = 0; k < Grains.Meta[0].DimensionValues.length; k++){
				var Grain = Grains.Meta[0].DimensionValues[k];
				var memberProps = Grain.MemberProperties;
				var dim , attribute;
				for(var l = 0; l < memberProps.length ; l++){
					var obj = memberProps[l];
					if(obj.PropertyName === "DimensionUsageDimensionName"){
						dim = obj.PropertyValue; }
					if(obj.PropertyName === "DimensionUsageDimensionAttribute"){
						attribute = obj.PropertyValue; }
				}
				if(dim != "Version"){
					var string1 = ' * ['+dim+'].['+attribute+']';
					query = query.concat(string1);
				}
			}
			query = query.concat(");\n");
		Log.Info('Granularity Identification Completed');
			
			return query;
	
	};
	
	
	
	var DataObject = 'Select ([Data Object].[Data Object].['+DataObjectPassed+'] * [Version].[Version Name].['+Version+'] ) on row, ({Measure.[Member Creation Input Dimension]}) on column;';
	var DataObjectOutput = queryModule.select(DataObject);
	Log.Info(DataObjectOutput);
	Log.Info("lsResponse ..." + Object.getOwnPropertyNames(DataObjectOutput));
	str = JSON.stringify(DataObjectOutput);
	Log.Info(str);
	
	var DataObjectCellSet = cellsetModule.createCellSet(DataObjectOutput);
	
	if(DataObjectCellSet.rowCount > 0){
			Log.Info("First Data Object Run Started");
			for (var i = 0; i < DataObjectCellSet.rowCount ; i++){
				var DataObjectName = DataObjectCellSet.row(i).cell(0).Name;
				var DimensionName = DataObjectCellSet.row(i).cell(2);
				Log.Info(DataObjectName);
				Log.Info(DimensionName);
				Create_member(DataObjectName, DimensionName);
		}
			Log.Info("First Data Object Run Completed");
	}
	
}; 
	
return {
	BulkMemberCreate:BulkMemberCreate
	};
});

