        }

        StartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+StartDate+'\")).relatedmembers([IO Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket is ' +StartBucket);

        HistoricalStartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+HistoricalStartDate+'\")).relatedmembers([IO Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +HistoricalStartBucket);

        EndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+EndDate+'\")).relatedmembers([IO Planning Bucket]).element(0).Name;').Result;
        Log.Info('End Bucket is ' +EndBucket);

        HistoricalEndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+HistoricalEndDate+'\")).relatedmembers([IO Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +HistoricalEndBucket);

        status = updateMEIOPlanningParameters(StartBucketSP,EndBucketSP,StartBucket , EndBucket ,HistoricalStartBucket , HistoricalEndBucket , Version);
        Log.Info(status);
    }

    else if (Solution == "DDMRP"){
        Log.Info("DDMRP Planning parameters Update started");
		Log.Info("[DDMRP CommonSolverStartAndEndDate JS] => Disable Plan is " + disableplan);

        // var StartDate = parsedParams.StartDate.split("T")[0];
        // var EndDate = parsedParams.EndDate.split("T")[0];
        var HistoricalStartDate = parsedParams.HistoricalStartDate;
        var HistoricalEndDate = parsedParams.HistoricalEndDate;
        var FutureStartDate = parsedParams.FutureStartDate;
        var FutureEndDate = parsedParams.FutureEndDate;

        var queryforNumberOfBuckets = 'Select ([Version].[Version Name].[' + Version + '] ) on row, ({Measure.[DDMRP Number of Planning Buckets for Buffer Profiling]}) on column;'
        var lsResponse = queryModule.select(queryforNumberOfBuckets);
        var datacellsetforNumberOfBuckets = cellsetModule.createCellSet(lsResponse);
        var NumberOfBuckets = datacellsetforNumberOfBuckets.row(0).cell(datacellsetforNumberOfBuckets.measureColumnIndex('DDMRP Number of Planning Buckets for Buffer Profiling'));
        Log.Info('Number Of Buckets ' +NumberOfBuckets);

        var lsresponseforstartdate =  queryModule.select('select (&CurrentDay);');
        var datacellsetforstartdate = cellsetModule.createCellSet(lsresponseforstartdate);
        var stdate =  datacellsetforstartdate.row(0).cell(datacellsetforstartdate.memberColumnIndex('Time','Day'));
        var StartdateName = stdate.Name
        Log.Info("Start date ... " + StartdateName);

        if(NumberOfBuckets === null){
            RuleOutputToUI = {Status: 'Error',Message: 'Number of Planning Buckets for Buffer Profiling is Null'};
            return;
        }
        else{
            var NumberOfBucketsminusone = NumberOfBuckets -1;
            var lsresponseforenddate = queryModule.select('select (&CurrentDay.['+ StartdateName +'].relatedmembers([DDMRP Planning Bucket]).element(0).leadoffset('+ NumberOfBucketsminusone +'));');
            var datacellsetforenddate = cellsetModule.createCellSet(lsresponseforenddate);
            var enddate =  datacellsetforenddate.row(0).cell(datacellsetforenddate.memberColumnIndex('Time','DDMRP Planning Bucket'));
            var enddateName = enddate.Name
            Log.Info("End date is ... " + enddateName);
        }

        var StartBucket = StartdateName;
        // StartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+StartDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket is ' +StartBucket);

        HistoricalStartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+HistoricalStartDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +HistoricalStartBucket);

        FutureStartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+FutureStartDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +FutureStartBucket);

        var EndBucket = enddateName;
        // EndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+EndDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('End Bucket is ' +EndBucket);

        HistoricalEndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+HistoricalEndDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +HistoricalEndBucket);

        FutureEndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+FutureEndDate+'\")).relatedmembers([DDMRP Planning Bucket]).element(0).Name;').Result;
        Log.Info('Start Bucket Historical is ' +FutureEndBucket);

        status = updateDDMRPPlanningParameters(StartBucket , EndBucket ,HistoricalStartBucket , HistoricalEndBucket ,FutureStartBucket, FutureEndBucket,Version,NumberOfBuckets);
        Log.Info(status);
    }


    else if (Solution == "DP"){

        var CurrentDate = parsedParams.CurrentDate.split("T")[0];

        CurrentDay = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+CurrentDate+'\")).element(0).Name;').Result;
        Log.Info('Current Day is ' +CurrentDay);

        var SetWeekIsCurrent = 'updatemember(Time.[Day]={,"'+ CurrentDay +'"},[Time].[Day$IsCurrent]={TRUE,});';
        Log.Info("SetWeekIsCurrent ... " + SetWeekIsCurrent);
        queryModule.update(SetWeekIsCurrent);

        Log.Info("Current Bucket has been Updated for Demand Planning");
    }
	else if (Solution == "CP"){

        var CurrentDate = parsedParams.CurrentDate.split("T")[0];

        CurrentDay = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+CurrentDate+'\")).element(0).Name;').Result;
        Log.Info('Current Day is ' +CurrentDay);

        var SetWeekIsCurrent = 'updatemember(Time.[Day]={,"'+ CurrentDay +'"},[Time].[Day$IsCurrent]={TRUE,});';
        Log.Info("SetWeekIsCurrent ... " + SetWeekIsCurrent);
        queryModule.update(SetWeekIsCurrent);

        Log.Info("Current Bucket has been Updated for Commercial Planning");
	}

	else if (Solution == "Month"){

		var CurrentDate = parsedParams.CurrentDate.split("T")[0];
        var StartDate = parsedParams.StartDate.split("T")[0];
        var EndDate = parsedParams.EndDate.split("T")[0];

		CurrentDay = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+CurrentDate+'\")).element(0).Name;').Result;
        Log.Info('Current Day is ' +CurrentDay);

		var SetMonthIsCurrent = 'updatemember(Time.[Day]={,"'+ CurrentDay +'"},[Time].[Day$IsCurrent]={TRUE,});';
        Log.Info("SetMonthIsCurrent ... " + SetMonthIsCurrent);
        queryModule.update(SetMonthIsCurrent);

		CurrentBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+CurrentDate+'\")).relatedmembers([Month]).element(0).Name;').Result;
        Log.Info('Current Bucket is ' +CurrentBucket);

        StartBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+StartDate+'\")).relatedmembers([Month]).element(0).Name;').Result;
        Log.Info('Start Bucket is ' +StartBucket);

        EndBucket = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+EndDate+'\")).relatedmembers([Month]).element(0).Name;').Result;
        Log.Info('End Bucket is ' +EndBucket);

        status = updateMonthlyPlanningParameters(CurrentBucket , StartBucket , EndBucket , Version);
        Log.Info(status);

	}

    else {
        Log.Info("Recieved Solution Type : " + "\"'+Solution+'\"");
        Log.Info("Verify valid solution type is passed to scripts. Valid inputs are \"MP \", \"OP\", \"MEIO\", \"DP\", \"CP\"");
    }

    if (disableplan){
    	Log.Info("Enable Plan....");
    	var enable = 'enable plan;';
    	queryModule.update(enable);
	}


};

return {
	SolverStartAndEndDate:SolverStartAndEndDate
	};
});

