define('o9.referencemodel.GenerateRowCount', ['o9/data/query', 'o9/data/cellset'], function () {
    var GenerateRowCount = function(o9Params){
	Log.Info("Started Java Script o9.referencemodel.GenerateCount");
	var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	var ParsedParams = JSON.parse(o9Params);
	Log.Info('o9 Params.....'+ o9Params);
	Log.Info('Passed Fields'+ JSON.stringify(ParsedParams));
	var Version = ParsedParams.Version;
	var RunID = ParsedParams.RunID;
	var Day = ParsedParams.Day;
	var ActionType = ParsedParams.ActionType;
	var Product = ParsedParams.Product;
	var User = ParsedParams.User;
	var RunIDParent = ParsedParams.RunIDParent;
	var PerformanceFlag = ParsedParams.PerformanceFlag;
	
	// To generate row count for fact data
	var FactDataRowCount = function(MeasureValues,DimMembers){
		var Grainquery = 'select (_SchemaPlan.[_Measure].['+MeasureValues+'].relatedmembers([_MeasureGroup]).relatedmembers([_DimensionUsage]) ) on row, () on column include memberproperties {_SchemaPlan.[_DimensionUsage],[DimensionUsageDimensionName],[DimensionUsageDimensionAttribute]};';
			var Grains = queryModule.select(Grainquery);
			var queryForFactData = 'Select (&CWV * {DistinctCount(';
			for(var k = 0; k < Grains.Meta[0].DimensionValues.length; k++){
				var Grain = Grains.Meta[0].DimensionValues[k];
				var memberProps = Grain.MemberProperties;
				var dim , attribute;
				for(var l = 0; l < memberProps.length ; l++){
					var obj = memberProps[l];
					if(obj.PropertyName === "DimensionUsageDimensionName"){
						dim = obj.PropertyValue; }
					if(obj.PropertyName === "DimensionUsageDimensionAttribute"){
						attribute = obj.PropertyValue; }
				}
				if(dim != "CurrentWorkingView"){
					var string1 = ' ['+dim+'].['+attribute+'] *';
					queryForFactData = queryForFactData.concat(string1);
				}
			}
			queryForFactData = queryForFactData.concat('Measure.['+MeasureValues+']) as Transient.[RowCount]}) on row, () on column;');
			var getFactData = queryModule.select(queryForFactData);
			Log.Info("Fact Data Count" +  JSON.stringify(getFactData));
			var FetchFactDataCount = cellsetModule.createCellSet(getFactData);
			Log.Info("Rows returned ..." + FetchFactDataCount.rowCount);
			if (FetchFactDataCount.rowCount>0){
				var FactDataCount = FetchFactDataCount.row(0).cell(FetchFactDataCount.measureColumnIndex('RowCount'));
				Log.Info("FactDataCount..." +FactDataCount );
				var CopyFactCountToMeasure = 'Cartesian scope:(&CWV * Action.[RunID].['+RunID+']*&CurrentDay*[Row Count].[Row Count].['+DimMembers+']);Measure.[Row Count] = '+FactDataCount+';end scope;';
				queryModule.update(CopyFactCountToMeasure);
				Log.Info("Assigning Counts to Measure " +  CopyFactCountToMeasure);
			}
			else {
				// assign zero if there are no records
			  	var CopyZeroFactCountToMeasure = 'Cartesian scope:(&CWV * Action.[RunID].['+RunID+']*&CurrentDay*[Row Count].[Row Count].['+DimMembers+']);Measure.[Row Count] = 0;end scope;';
				queryModule.update(CopyZeroFactCountToMeasure);
				Log.Info("Assigning Zero to Measure " +  CopyZeroFactCountToMeasure);
				Log.Info("Since no rows returned , not updating any Row count measure");
			} 
			
	};
	// To generate row count for master data
	var MasterDataRowCount = function(MeasureValues,DimMembers){
		var queryForDimData = 'Select(&'+MeasureValues+'.count);';
		var getDimCount = queryModule.select(queryForDimData);
		Log.Info("Dimension Row count" +  JSON.stringify(getDimCount));
		var MasterDataCount = getDimCount.Result;
		if(MasterDataCount>0){
			var CopyDimCountToMeasure = 'Cartesian scope:(&CWV * Action.[RunID].['+RunID+'] *&CurrentDay *[Row Count].[Row Count].['+DimMembers+']);Measure.[Row Count] = '+MasterDataCount+';end scope;';
		    queryModule.update(CopyDimCountToMeasure);
		    Log.Info("Assigning Counts to Measure " +  CopyDimCountToMeasure);
		}
		else {
			// assign zero if there are no records
			var CopyZeroDimCountToMeasure = 'Cartesian scope:(&CWV * Action.[RunID].['+RunID+'] *&CurrentDay *[Row Count].[Row Count].['+DimMembers+']);Measure.[Row Count] = 0;end scope;';
		    queryModule.update(CopyZeroDimCountToMeasure);
		    Log.Info("Assigning Zero to Measure " +  CopyZeroDimCountToMeasure);
		}

	};
	// To get all row count attribute members of Row Count
	var DefinedMeasureForCount = function(DefinedMeasureParams){
		var ParametersForCount = cellsetModule.createCellSet(DefinedMeasureParams);
		if(ParametersForCount.rowCount > 0){
			for (var i = 0; i < ParametersForCount.rowCount ; i++){
			var MeasureValues = ParametersForCount.row(i).cell(ParametersForCount.measureColumnIndex('Row Count Definition'));
			var DimMembers = ParametersForCount.row(i).cell(ParametersForCount.memberColumnIndex('Row Count','Row Count')).Name;
			var DimType = ParametersForCount.row(i).cell(ParametersForCount.memberColumnIndex('Row Count','Type')).Name;
			var DimActionType = ParametersForCount.row(i).cell(ParametersForCount.memberColumnIndex('Row Count','Action Type')).Name;
			Log.Info("Measure....." + MeasureValues);
			Log.Info("DimMembers...." + DimMembers);
			Log.Info("DimType......." + DimType);
			Log.Info("DimActionType......." + DimActionType);
			if(MeasureValues !== null){
				// To get Namedsets value with row count members for master data
				if(DimType.includes('Master Data Count') === true){
					Log.Info("Calling Master data row count updation function");
					MasterDataRowCount(MeasureValues,DimMembers);
				}
				// To get measure names with row count members for fact data
				else if(DimType.includes('Fact Data Count') === true){
					Log.Info("Calling Fact Data Row Count updation function");
					FactDataRowCount(MeasureValues,DimMembers);
				}
				else {
					Log.Info("Invalid Dimension Type");
					}
			}
		}
		}
	};	
		Log.Info("Executing Javasrcipt");
		////--For tracking the start time
		var queryForScenarioCount = 'Select ([Version].[Version Name].filter(#.Key <=0).count);';
		var getScenarioCount = queryModule.select(queryForScenarioCount);
		Log.Info("ScenarioCount" +  JSON.stringify(getScenarioCount));
		var ScenarioCount = getScenarioCount.Result;
		var queryForVersionCount = 'Select ([Version].[Version Name].filter(#.Key >=0).count);';
		var getVersionCount = queryModule.select(queryForVersionCount);
		Log.Info("VersionCount" +  JSON.stringify(getVersionCount));
		var VersionCount = getVersionCount.Result;
		var ActionStartProcedure = 'exec procedure ActionStart {"User" : "'+User+'" , "RunID" : "'+ RunID +'","RunIDParent" : "'+ RunIDParent +'", "ActionType" : "'+ActionType+'", "Product" : "'+Product+'","ScenarioCount" : '+ScenarioCount+',"VersionCount" : '+VersionCount+',"ScenarioName" : "'+Version+'"};';
		var ExecuteActionStartProcedure = queryModule.select(ActionStartProcedure);
		// Procedure to create members of row count
		if (PerformanceFlag === 'true'){
		if (ActionType == 'Create Network Weekly'){
			var queryForExecution = 'exec procedure SeedDataCreateNetworkWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataCreateNetworkWeekly");
		}
		else if (ActionType == 'Where Used Weekly'){
			var queryForExecution = 'exec procedure SeedDataWhereUsedWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure Where Used Weekly");
		}
		else if (ActionType == 'Transaction Data Weekly'){
			var queryForExecution = 'exec procedure SeedDataTransactionDataWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataTransactionDataWeekly");
		}
		else if (ActionType == 'Demand Prioritization Weekly'){
			var queryForExecution = 'exec procedure SeedDataAdvancedDemandPrioritizationWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataAdvancedDemandPrioritizationWeekly");
		}
		else if (ActionType == 'Demand Netting at Month Weekly'){
			var queryForExecution = 'exec procedure SeedDataDemandNettingatMonthWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataDemandNettingatMonthWeekly");
		}
		else if (ActionType == 'Advanced Netting Solver with RTF Run Weekly'){
			var queryForExecution = 'exec procedure SeedDataAdvancedDemandNettingSolverwithRTF {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataAdvancedDemandNettingSolverwithRTF");
		}
		else if (ActionType == 'Advanced Netting Python with RTF Run Weekly'){
			var queryForExecution = 'exec procedure SeedDataAdvancedNettingPythonwithRTF {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataAdvancedNettingPythonwithRTF");
		}
		else if (ActionType == 'Skip Netting Weekly'){
			var queryForExecution = 'exec procedure SeedDataSkipNettingWeekly {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataSkipNetting");
		}
		else if (ActionType == 'Advanced Demand Netting solver without RTF Run Weekly'){
			var queryForExecution = 'exec procedure SeedDataAdvancedDemandNettingSolverWithoutRTF {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataAdvancedDemandNettingSolverWithoutRTF");
		}
		else if (ActionType == 'Advanced Netting Python without RTF Run Weekly'){
			var queryForExecution = 'exec procedure SeedDataAdvancedNettingPythonwithoutRTF {"Version" : "CurrentWorkingView"};';
			Log.Info("exec procedure SeedDataAdvancedNettingPythonwithoutRTF");
		}
		else if (ActionType == 'Where Used Daily'){
			var queryForExecution = 'exec procedure SeedDataWhereUsedDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedDataWhereUsedDaily");
		}
		else if (ActionType == 'Demand Netting at Month Daily'){
			var queryForExecution = 'exec procedure SeedDataDemandNettingatMonthDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedDataDemandNettingatMonthDaily");
		}
		else if (ActionType == 'Skip Netting Daily'){
			var queryForExecution = 'exec procedure SeedDataSkipNettingDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedDataSkipNettingDaily");
		}
		else if (ActionType == 'FinancialsDaily'){
			var queryForExecution = 'exec procedure SeedFinancialsDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedFinancialsDaily");
		}
		else if (ActionType == 'Demand Prioritization Daily'){
			var queryForExecution = 'exec procedure SeedDataDemandPrioritizationDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedDataBasicDemandPrioritizationDaily");
		}
		else if (ActionType == 'Create Network Daily'){
			var queryForExecution = 'exec procedure SeedDataCreateNetworkDaily {"Version" : "' + Version + '"};';
			Log.Info("exec procedure SeedDataCreateNetworkDaily");
		}
		else {
			Log.Info("Invalid Action Type");
		}				
		var ExecuteProcedure = queryModule.select(queryForExecution);
		Log.Info("Seeddata procedure Executed");
		// To Fetch Namedsets and Measure Names from Row Count Definition measure
		var getDefinedMeasureValues = 'select ( &CWV *[Row Count].[Type] *[Row Count].[Action Type].['+ActionType+']*[Row Count].[Row Count]) on row,({Measure.[Row Count Definition]}) on column;';	
		var DefinedMeasureValues = queryModule.select(getDefinedMeasureValues);
		DefinedMeasureForCount(DefinedMeasureValues);
		}
		else {
		Log.Info("Javasrcipt to generate row counts not executed");
		}
	};

return {
	GenerateRowCount:GenerateRowCount
	};
});

