		var ScenarioName = 'Deleteversion (\"'+x+'\");';
		queryModule.update(ScenarioName);
		Log.Info("DeletedScenario ... " +  x );
		
		
		
	}
};   
	
return {
	DeleteScenario:DeleteScenario
	};
});

