	var CurrentTimeRollover= function(o9Params){
    var parsedParams = JSON.parse(o9Params);
    //var Version = parsedParams.Version;
    //var Solution = parsedParams.Solution;
    var disableplan = parsedParams.Plan;
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');

  if (disableplan){
		
		Log.Info("Disable Plan....");
		var disable = 'disable plan;';
		queryModule.update(disable);
	}
 
        var CurrentDate = parsedParams.CurrentDate.split("T")[0];

        CurrentDay = queryModule.select('SELECT Time.Day.filter(#.Key == todatetime(\"'+CurrentDate+'\")).element(0).Name;').Result;
        Log.Info('Current Day is ' +CurrentDay);
    
        var SetWeekIsCurrent = 'updatemember(Time.[Day]={,"'+ CurrentDay +'"},[Time].[Day$IsCurrent]={TRUE,});';
