define('o9.SCDMABDMTransformationsCreateRow.MultiDestinations', ['o9/data/query', 'o9/data/cellset'], function () {
	var MultiDestinations = function(o9Params){
    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');
    var Version = parsedParams.Version;

    var DataObject = parsedParams.DataObject;

    Log.Info('..................Starting with a Smile..................');

    var execibplselect = 'Select ([Version].[Version Name] * [Sequence ID].[Sequence ID] * [Data Object].[Data Object].['+ DataObject +'] ) on row, ({Measure.[ABDM Create Members Required], Measure.[ABDM Filter], Measure.[ABDM Group By], Measure.[ABDM Join Key], Measure.[ABDM Join Type], Measure.[ABDM Order By], Measure.[ABDM Source Column], Measure.[ABDM Source Table], Measure.[ABDM Target Column], Measure.[ABDM Target Table]}) on column;'

    Log.Info('select IBPL Executed...');    
    var dataCellSetCAMAllocation = cellsetModule.createCellSet(queryModule.select(execibplselect));
    var maxSeqNum = dataCellSetCAMAllocation.rowCount;

    var nextSeqNum = maxSeqNum + 1;
    Log.Info('maxSeqNum Type is ===== ');
    Log.Info(typeof maxSeqNum);
    Log.Info('nextSeqNum ===== ' + nextSeqNum);
    Log.Info('nextSeqNum Type is ===== ');
    Log.Info(typeof nextSeqNum);
    
    var CreateSeqID = 'createmember([Sequence ID].[Sequence ID]={,"'+ nextSeqNum +'"});' ;
    Log.Info('Create Seq ID ===== ' + CreateSeqID);
    queryModule.update(CreateSeqID);
    Log.Info('Seq ID Created...');

    var execibpl = 'Cartesian scope:  ([Version].[Version Name].['+ Version +'] * [Sequence ID].[Sequence ID].['+ nextSeqNum +'] * [Data Object].[Data Object].['+ DataObject +'] ); Measure.[ABDM Create Members Required] = "false"; Measure.[ABDM Filter] = ""; Measure.[ABDM Group By] = ""; Measure.[ABDM Join Key] = ""; Measure.[ABDM Order By] = ""; Measure.[ABDM Source Column] = ""; Measure.[ABDM Source Table] = ""; Measure.[ABDM Target Column] = ""; Measure.[ABDM Target Table] = ""; end scope;'

    Log.Info('IBPL Executed...');    
    Log.Info(execibpl);

    queryModule.update(execibpl);
    Log.Info('....................Ending with a Cheesy Smile....................');
};
return {
	MultiDestinations:MultiDestinations
	};
});

