define('o9.ekgconfig.validateInput', ['o9/data/query', 'o9/data/cellset'], function () {

    var validateAndLoad = async function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');


        var JsonStatusIBPL=`EXEC plugin instance GetJSONStatus for measures {[Value]} using scope (Version.[Version Name].[CurrentWorkingView]) using arguments {([ExecutionMode], "MediumWeight")};`;
        queryModule.update(JsonStatusIBPL);

        let JSONStatusFlagIBPL = "Select ([Version].[Version Name].[CurrentWorkingView] * [o9MKPipelineSettings].[PipelineSettingsName].[JSONStatusMessage]) on row, ({Measure.[Value]}) on column;";
        let JSONStatusFlagPivot=cellsetModule.createCellSet(queryModule.select(JSONStatusFlagIBPL));
        let JSONStatusFlag=JSONStatusFlagPivot.row(0).cell(2);

        if(JSONStatusFlag!=='Success'){
            RuleOutputToUI = { Status: 'Error', Message: JSONStatusFlag};
            return;
        }

        var flag_query = `
            scope: (&CWV *[o9MKTime].[Day] * [o9EKGServices].[o9EKGService] * [o9EKGSequence].[o9EKGSequenceNumber]);
            Measure.[o9EKGLogLatest] = 0 ;
            end scope;`;

        queryModule.update(flag_query);

        var executeBlock = function (schemaGeneration, incrementalPipeline, parallel) {

            if (schemaGeneration.trim() !== '') {
                var set_schema_flag = `
                cartesian scope: ([Version].[Version Name].[CurrentWorkingView] * [o9MKPipelineSettings].[PipelineSettingsName].[Incremental Schema Generation]);
                Measure.[Value]=${schemaGeneration};
                end scope;`;

                queryModule.update(set_schema_flag);
            }

            if (incrementalPipeline.trim() !== '') {
                var set_pipeline_flag = `
                cartesian scope: ([Version].[Version Name].[CurrentWorkingView] * [o9MKPipelineSettings].[PipelineSettingsName].[Incremental Pipeline Generation]);
                Measure.[Value]=${incrementalPipeline};
                end scope;`;

                queryModule.update(set_pipeline_flag);
            }

            if (parallel === 'True') {
                var parallel_ibpl = `
                    exec parallel begin
                        Block begin 
                            EXEC plugin instance ExecuteDeltaPipeline for measures {[Value]} using scope (Version.[Version Name].[CurrentWorkingView]);
                        End
                        Block begin 
                            EXEC plugin instance AutoGenPipeline_v3_threading for measures {[Value]} using scope (Version.[Version Name].[CurrentWorkingView]);
                        End
                    end;`;

                queryModule.update(parallel_ibpl);

            } else {

                if (schemaGeneration.trim() !== '') {
                    var delta_table_ibpl = `EXEC plugin instance ExecuteDeltaPipeline for measures {[Value]} using scope (Version.[Version Name].[CurrentWorkingView]);`;
                    queryModule.update(delta_table_ibpl);

                } 

                else if (incrementalPipeline.trim() !== '') {
                    var pipeline_ibpl = `EXEC plugin instance AutoGenPipeline_v3_threading for measures {[Value]} using scope (Version.[Version Name].[CurrentWorkingView]);`;
                    queryModule.update(pipeline_ibpl);
                }
            }

        };

        if (parsedParams.Delta_Table_Instantiation === 'Incremental Create') { 
            if (parsedParams.Pipeline_Creation_Method === 'Incremental Create') {
                executeBlock('True', 'True', 'True');
            }

            else if (parsedParams.Pipeline_Creation_Method === 'Delete & Recreate') {
                executeBlock('True', 'False', 'True');
            }

            else if (parsedParams.Pipeline_Creation_Method === 'No') {
                executeBlock('True', '', 'False');
            }
        }

        else if (parsedParams.Delta_Table_Instantiation === 'Delete & Recreate') {
            if (parsedParams.Pipeline_Creation_Method === 'Incremental Create') {
                executeBlock('False', 'True', 'True');
            } 

            else if (parsedParams.Pipeline_Creation_Method === 'Delete & Recreate') {
                executeBlock('False', 'False', 'True');
            } 

            else if (parsedParams.Pipeline_Creation_Method === 'No') {
                executeBlock('False', '', 'False');
            }
        }

        else if (parsedParams.Delta_Table_Instantiation === 'No') {
            if (parsedParams.Pipeline_Creation_Method === 'Incremental Create') {
                executeBlock('', 'True', 'False');
            } else if (parsedParams.Pipeline_Creation_Method === 'Delete & Recreate') {
                executeBlock('', 'False', 'False');
            } else if (parsedParams.Pipeline_Creation_Method === 'No') {
                RuleOutputToUI = { Status: 'Error', Message: 'You must choose at least 1 option.' };
                return;
            }
        }
        return;
    };

    return {
        validateAndLoad: validateAndLoad
    };
});

