define('o9.EKG.populateTenantRule', ['o9/data/query', 'o9/data/cellset'], function () {

    let logicalOperators = ["AND", "OR", "NOT"]
    let comparisonOperators = ["!=", "=", ">", "<", "BETWEEN", " IN ", " LIKE ", " IS NOT NULL ", " IS NULL "]

    var parseRuleExpression = function (ruleExpression) {
        // This function return the parsed Dimension and Attribute present in RuleExpression
        let trimmedExpression = ruleExpression.trim();

        if (trimmedExpression == "<any>") return false;

        let parts = null;

        // iterate through and when get the first comparision operator, return this
        for (let idx = 0; idx < trimmedExpression.length; idx++) {
            for (let op of comparisonOperators) {
                let opLength = op.length;
                if (trimmedExpression.slice(idx, idx + opLength).toLowerCase() == op.toLowerCase()) {
                    parts = trimmedExpression.split(op);
                    break;
                }
            }
            if (parts) break;
        }

        if (parts == null) return false;

        return parts[0].trim();
    }

    var matchFieldName = function (fieldName1, fieldName2) {
        // This function will loosely match two field Names
        // Rules: Ignore Spaces, [, ]

        fieldName1 = fieldName1.replace(/[\[\]]/g, '');
        fieldName2 = fieldName2.replace(/[\[\]]/g, '');

        return fieldName1 == fieldName2;
    }

    var matchDimFromFieldName = function (fieldName1, fieldName2) {
        fieldName1 = fieldName1.trim().replace(/[\[\]]/g, '');
        fieldName2 = fieldName2.trim().replace(/[\[\]]/g, '');
    }

    var getDimNameFromFieldName = function (fieldName) {
        fieldName = fieldName.trim();

        fieldName = fieldName.replace(/[\[\]]/g, '')
        fieldName = fieldName.split(".")

        return fieldName[0].trim();
    }

    var isDimField = function (fieldName) {
        if (fieldName == undefined || fieldName == null || fieldName == "") return false;
        if (fieldName.includes('.')) {
            return true;
        } else {
            return false;
        }
    }

    var populateTenantRule = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const Project = parsedParams.Project;

        Log.Info("Started populateTenantRule Plugin");


        const sysGen = "System Generated"

        // clear old data routing

        let deleteOldDataQuery = `scope: (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Name] * [o9EKG Tenant].[Tenant]);
                                        Measure.[o9EKG Tenant Rule] = Null;
                                end scope;`;
        queryModule.update(deleteOldDataQuery)

        // get list of all dimension table and field ID

        let tableFieldQuery = `(Select (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID] * { Measure.[o9EKG LS Table Type], Measure.[o9EKG Field Name], Measure.[o9EKG Table Included]})).filter(Measure.[o9EKG Table Included] == 1);`;
        let tableFieldResult = cellsetModule.createCellSet(queryModule.select(tableFieldQuery));

        let tableFieldMapping = {}; // {fieldName: [tableName1, tableName2]}
        let fieldTableMapping = {};
        let dimTableList = {};
        let factTableList = {};

        let tableFieldCount = tableFieldResult.rowCount;

        for (let tableFieldIdx = 0; tableFieldIdx < tableFieldCount; tableFieldIdx++) {
            let tableName = tableFieldResult.row(tableFieldIdx).cell(2).Name;
            let fieldName = tableFieldResult.row(tableFieldIdx).cell(5);
            let lsDimType = tableFieldResult.row(tableFieldIdx).cell(4);

            if (fieldName == "") continue;

            if (lsDimType == "Dimension") {
                dimTableList[tableName] = true;
            }
            else if (lsDimType == "Fact") {
                factTableList[tableName] = true;
            }


            if (!tableFieldMapping.hasOwnProperty(fieldName)) {
                tableFieldMapping[fieldName] = []
            }

            tableFieldMapping[fieldName].push(tableName);

            if (!fieldTableMapping.hasOwnProperty(tableName)) {
                fieldTableMapping[tableName] = []
            }

