define('o9.EKG.validateField', ['o9/data/query', 'o9/data/cellset'], function () {

  var validateField = function(o9Params){

    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');
    Log.Info('PRINT ---' + parsedParams.FieldName);
    let stringifiedParams = JSON.stringify(parsedParams);
    Log.Info('PRINTING FOR PROJECT DETAILS: '+ stringifiedParams);

    // Condition: Field Name should not contain characters `{},!$@%^&*
    if(/[{},'!$@%^&*]/.test(parsedParams.FieldName)) {
      RuleOutputToUI = {
        Status: 'Error',
        Message: 'Field Name should not contain characters `{},!$@%^&*'
      };
      return RuleOutputToUI;
    }


    // Condition: Field Name should be unique within a table
    var ExistingIls = `(Select (&CWV * [o9EKG Customer].[Project Name].[${parsedParams.Project}] * [o9EKG Table].[Field ID] *[o9EKG Table].[Table Name].[${parsedParams.TableName}]) on row, ({ Measure.[o9EKG Field Name]}) on column).filter(Measure.[o9EKG Field Name]=="${parsedParams.FieldName}"); 
`;
    Log.Info("PRINT --- " +ExistingIls );
    var temp = cellsetModule.createCellSet(queryModule.select(ExistingIls));
    Log.Info("PRINT" +temp.rowCount);

    if(temp.rowCount > 0) {
      RuleOutputToUI = {
        Status:'Error',
        Message:'This Field already exists. Please enter a new name'
      };
    }
    else {
      RuleOutputToUI = {
        Status: 'Success',
        Message: 'New Field added'
      };
    }

    return RuleOutputToUI;
  };

  return {
    validateField: validateField
  };
});

