define('o9.EKG.validateTable', ['o9/data/query', 'o9/data/cellset'], function () {

    var validateTable = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        // Condition: Table Name cannot be greater than 50 characters
        if (parsedParams.TableName.length > 50) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Table Name cannot be greater than 50 characters.'
            };
            return RuleOutputToUI;
        }

        // Condition: Table Name can only contain alphabets(a-z, A-Z), numbers(0-9), and _
        if (!/^[a-zA-Z0-9_]+$/.test(parsedParams.TableName)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Table Name can only contain alphabets (a-z, A-Z), numbers(0-9), and underscore(_).'
            };
            return RuleOutputToUI;
        }

        // Condition: Table Name cannot be only numbers
        if (/^\d+$/.test(parsedParams.TableName)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Table Name cannot be only numbers.'
            };
            return RuleOutputToUI;
        }

        // Condition: Table Name should not start with numbers
        if (/^\d/.test(parsedParams.TableName)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Table Name should not start with numbers.'
            };
            return RuleOutputToUI;
        }



        Log.Info('PRINT ---' + parsedParams.TableName);
        let Table_name = parsedParams.TableName;

        var ExistingIls = `Select ( [o9EKG Customer].[Project Name] * [o9EKG Table].[Table Name].filter(#.Name like "${Table_name}")) on row, ({Measure.[o9EKG Table Included]}) on column where {&CWV, Measure.[o9EKG Table Included]==1} orderby [o9EKG Customer].[Project Name].Key desc;`;
        Log.Info("PRINT --- " + ExistingIls);
        var temp = cellsetModule.createCellSet(queryModule.select(ExistingIls));
        Log.Info("PRINT" + temp.rowCount);

        // Condition: Table Name should be unique in the entire tenant (EKG configurator) and not just the project (Name should not be differ in terms of casing)
        let table_flag = 0;
        Log.Info("Table " + Table_name);
        
        if (temp.rowCount > 0) {
            table_flag = 1;
            let Projects = []
            for (i = 0; i < temp.rowCount; i++) {
                let TableFromIBPL = temp.row(i).cell(1).Name
                if (TableFromIBPL.toLowerCase() === Table_name.toLowerCase()) {
                    let Project = temp.row(i).cell(0).Name
                    Projects.push(Project);
                }
            }
            Log.Info("Projects " + Projects);
            Log.Info("Project length " + Projects.length);

            if (Projects.length === 0) {
                table_flag = 0;
            }

            let ProjectsSet = new Set(Projects);

            Projects = [...ProjectsSet]

            if(Projects.length > 3){
                Projects = Projects.slice(0, 3)
            }

            let projectString = ""

            Projects.map((projectItem, idx) => {
                if (idx == 0) {
                    projectString += projectItem
                }
                else if (idx === Projects.length - 1) {
                    projectString += ` & "${projectItem}"`
                } else {
                    projectString += `, "${projectItem}"`
                }
            })

            let phrase = ""

            if (temp.rowCount == 1) {
                projectString = "project " + projectString
                phrase = "this project"
            }
            else {
                projectString = "projects " + projectString
                phrase = "these project"
            }

            errorMessage = `This table already exists in ${projectString}.\nPlease enter a new name or copy the table from ${phrase} to edit it here using the Copy Table button.`


        }


        if (table_flag > 0) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: errorMessage
            };
        }
        else {
            RuleOutputToUI = {
                Status: 'Success',
                Message: 'New Table added'
            };
        }

        return RuleOutputToUI;
    };

    return {
        validateTable: validateTable
    };
});

