define('o9.EKG.generateFieldID', ['o9/data/query', 'o9/data/cellset'], function () {

    var generateFieldID = function(o9Params){
  
    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');
    Log.Info("Started generateFieldID Plugin");
    
    // Query to get the last used custom field ID
    let ExistingIls = `Select ( [o9EKG Table].[Table Type] * [o9EKG Table].[Table Group] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID].filter(#.Name startswith "CF_") )  orderby [o9EKG Table].[Field ID].Key desc limit 1;`;
    let temp = cellsetModule.createCellSet(queryModule.select(ExistingIls));
    var ID;
    if( temp.row(0).cell(3) && temp.row(0).cell(3).Name){
     ID = temp.row(0).cell(3).Name;
     Log.Info(`ID if CF is present--------${ID}`);
    }else{
        ID = null;
        Log.Info(`ID if CF is not present--------${ID}`);
    }
    Log.Info('Last custom Field ID---------- '+ ID);
    var newID;
    if(ID){
        var IDNo = ID.substring(3);
        var newIDNo = parseInt(IDNo) + 1;
        newID = "CF_" + newIDNo;
        Log.Info("New Field ID: "+newID);
