define('o9.EKG.validateCustomFieldID', ['o9/data/query', 'o9/data/cellset'], function () {

    var validateCustomFieldID = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        Log.Info("Started validateCustomFieldID Plugin");

        let customFieldPrefix = parsedParams.CustomFieldPrefix;
        let Project = parsedParams.Project;


        Log.Info("Using Custom Field Prefix " + customFieldPrefix);

        /*
            Validation: 
                1. Case Senstive? Yes
                2. MaxLength of Prefix? 3
                3. Only Alphabets allowed? no numbers or special chars // only alphabet
                4. No whitespace? or auto trimming? // trimming
                5. Do we need to lock the prefix value once user has defined, or do we give them flexibility to change the prefix after defining
                6. Method to identify ODMEKG Tenant, (TenantID - from Admin EKG Configurator)
        */

        let trimmedPrefix = customFieldPrefix.trim();

        // Validation 1: Check if prefix is empty
        if (!trimmedPrefix) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: `Error: Prefix cannot be empty.`
            };
            return RuleOutputToUI;
        }

        // Validation 2: Check MaxLength of Prefix (3)
        if (trimmedPrefix.length > 3) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: `Error: Prefix cannot be longer than 3 characters.`
            };
            return RuleOutputToUI;
        }

        // Validation 3: Check if only alphabets are allowed
        if (!/^[a-zA-Z]+$/.test(trimmedPrefix)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: `Error: Prefix must contain only alphabetic characters.`
            };
            return RuleOutputToUI;
        }

        let isODMEKGTenant = false;
        let ODMEKGConfiguratorTenantID = "5935"
        let ODMEKGConfiguratorTenantURL = "https://mygcppmm.o9solutions.com"
        let isODMPrefixOverwritten = false;

        // list of restricted prefix (case - Insenstive)
        let restrictedPrefixes = ["F", "CF", "DF", "SF", "ZF"];

        // Validation 1: Should not be restricted prefix

        let tenantIDQuery = `Select (&CWV * {Measure.[o9EKG Tenant ID], Measure.[o9EKG Tenant URL]});`;
        // if tenant ID is for 'ODMEKGConfigurator' is defined and is same as tenant ID of ODMEKGConfigurator
        let tenantIDResult = cellsetModule.createCellSet(queryModule.select(tenantIDQuery));

        let tenantIDCount = tenantIDResult.rowCount;
        if (tenantIDCount > 0) {
            let tenantID = tenantIDResult.row(0).cell(1);
            let tenantUrl = tenantIDResult.row(0).cell(2);
            if (tenantID == ODMEKGConfiguratorTenantID && tenantUrl == ODMEKGConfiguratorTenantURL) {
                isODMEKGTenant = true;
            }
        }

        if (!isODMEKGTenant) {
            restrictedPrefixes.push("PMM")
        }

        let isARestrictedPrefix = restrictedPrefixes.includes(trimmedPrefix);

        if (isARestrictedPrefix) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: `Error: The following prefixes are restricted: ${restrictedPrefixes.join(', ')}. Please use a different prefix.`
            };
            return RuleOutputToUI;
        } else if (isODMEKGTenant && trimmedPrefix != "PMM") {
            trimmedPrefix = "PMM"
            isODMPrefixOverwritten = true;
        }

        let queryToWrite = `scope: (&CWV * [o9EKG Customer].[Project Name].[${Project}]);
            Measure.[o9EKG Custom Field Prefix] = "${trimmedPrefix}";
        end scope;`

        queryModule.update(queryToWrite)

        if (isODMPrefixOverwritten) {
            RuleOutputToUI = {
                Status: 'Success',
                Message: `<span style="color: blue;">Info: For ODMEKG Tenant, only PMM prefix is allowed.</span>`
            };
        } else {
            RuleOutputToUI = {
                Status: 'Success',
                Message: `<span style="color: green;">Success: Prefix is added successfully.</span>`
            };
        }
        return RuleOutputToUI;

    };

    return {
        validateCustomFieldID: validateCustomFieldID
    };
});

