define('o9.ekgconfig.saveProject2', ['o9/data/query', 'o9/data/cellset'], function () {
    const saveProject = function (o9Params) {
        const parsedParams = JSON.parse(o9Params);
        const queryModule = require('o9/data/query');
        const cellsetModule = require('o9/data/cellset');

        const project = parsedParams.Project;
        const newSequence = parsedParams.NewSeq;
        const createdBy = parsedParams.user;
        const creationTimestamp = parsedParams.now;

        // Helper function for logging and updating queries
        const logAndExecuteQuery = (logMessage, query) => {
            Log.Info(`<EKG - Save Project Logs> : ${logMessage} = ${query}`);
            queryModule.update(query);
        };


        const validationIBPLQuery = `(Select ([o9EKGSequence].[o9EKGSequenceNumber] * [o9EKG Customer].[Project Name].[${project}] * [Version].[Version Name].[CurrentWorkingView] ) on row, 
({ Measure.[o9EKG Validation Criticality]}) on column).filter(Measure.[o9EKG Validation Criticality] == "Error" );`;
 
        Log.Info(`<EKG - Save Project Logs> : Validation IBPL Check = ${validationIBPLQuery}`);
        let errorFlag = false;

        try {
            const validationRow = cellsetModule.createCellSet(queryModule.select(validationIBPLQuery));
            const errorText = validationRow.row(0).cell(5);
            const errorRow = validationRow.rowCount;
            Log.Info("<EKG - Save Project Logs> : " + errorText);
            Log.Error("<EKG - Error Text > : " + errorText);
            Log.Error("<EKG - Error Text errorRow> : " + errorRow);

            if (errorRow !== 0) {
                errorFlag = true;
                Log.Error("<EKG - Save Project Logs> : Validation Errors found. Exiting Execution...");
            } else {
                Log.Info("<EKG - Save Project Logs> : No validation errors found. Generating JSON data.");
            }
        } catch (error) {
            errorFlag = false;
            Log.Info("<EKG - Save Project Logs> : No validation errors found. Generating JSON data.(catch)"); // No errors found
        }


        // 7. Execute pipeline metadata if no errors
        if (!errorFlag) {
            const markLatestFalseQuery = `scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKGSequence].[o9EKGSequenceNumber]);Measure.[o9EKG Project Metadata IsLatest] = "FALSE";end scope;`;
            logAndExecuteQuery("Mark Latest Flag Query (Set FALSE)", markLatestFalseQuery);
    
            const markLatestTrueQuery = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKGSequence].[o9EKGSequenceNumber].[${newSequence}]);Measure.[o9EKG Project Metadata IsLatest] = "TRUE";end scope;`;
            logAndExecuteQuery("Mark Latest Flag Query (Set TRUE)", markLatestTrueQuery);
    
            // 5. Populate metadata measures
            const populateMetadataQuery = `scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKGSequence].[o9EKGSequenceNumber].[${newSequence}]);Measure.[o9EKG Project Metadata Created By] = "${createdBy}";Measure.[o9EKG Project Metadata Creation Timestamp] = "${creationTimestamp}";Measure.[o9EKG Project Metadata] = "ABC";end scope;`;
            logAndExecuteQuery("Populating Metadata Measures", populateMetadataQuery);

            const executePipelineMetaQuery = `EXEC plugin instance o9EKGPipeline_Meta for measures {[o9EKG Field Name]} using scope (&CWV * [o9EKG Customer].[Project Name].[${project}]);`;
            logAndExecuteQuery("Executing Pipeline Metadata Plugins", executePipelineMetaQuery);
            
            const successMessage = "Project saved successfully. JSON and API doc has been generated.".fontcolor('green');
            RuleOutputToUI = { Status: 'Success', Message: successMessage };
            return;
        } else {
            const errorMessage = "There are Validation Errors in this project. Please fix the errors and try again...".fontcolor('red');
            RuleOutputToUI = { Status: 'Success', Message: errorMessage };
            return;
        }
    };

    return {
        saveProject: saveProject
    };
});

