define('o9.EKG.toggleMultiTenant', ['o9/data/query', 'o9/data/cellset'], function () {

    var chunkArray = function (array, chunkSize) {
        let chunkedArray = [];

        for (let i = 0; i < array.length; i += chunkSize) {
            let chunk = array.slice(i, Math.min(array.length, i + chunkSize));
            chunkedArray.push(chunk);
        }

        return chunkedArray;
    }

    var joinArray = function (array) {
        return array.map(function (p) {
            return '"' + p + '"';
        }).join(',');
    }

    var toggleMultiTenant = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const project = parsedParams.Project;
        const status = parsedParams.status;
        const user = parsedParams.user;
        const now = parsedParams.now;
        let MT_Status = parsedParams.MT_Status;

        MT_Status = "" + MT_Status

        Log.Info(typeof MT_Status);
        Log.Info(MT_Status);


        const stripe_col_name = "o9_ekg_tenant_stripe";

        Log.Info("Started toggleMultiTenant Plugin");

        // status can be New, Include, Exclude, Delete


        let allLSUTablesQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Table Type].[LS_Upload] * [o9EKG Table].[Table Group] * [o9EKG Table].[Table Name] * {Measure.[o9EKG Table Included], Measure.[o9EKG Field Sub Block Association Name], Measure.[o9EKG Business Characteristics Field Association Name]});`;
        let allLSUTablesResult = cellsetModule.createCellSet(queryModule.select(allLSUTablesQuery));

        let numLSUTables = allLSUTablesResult.rowCount;

        // get numLSUTables field IDs

        let getLastCustomFieldIDQuery = `Select ([o9EKG Table].[Field ID].filter(#.Name startswith "CF_")) orderby [o9EKG Table].[Field ID].Key desc limit 1;`;
        let getLastCustomFieldIDResult = cellsetModule.createCellSet(queryModule.select(getLastCustomFieldIDQuery));

        let lastCFID = 0;
        if (getLastCustomFieldIDResult.rowCount > 0) {
            lastCFID = getLastCustomFieldIDResult.row(0).cell(0).Name
            lastCFID = Number(lastCFID.replace("CF_", ""));
        }

        // add a check to verify that no other field should be present in lastCFID to lastCFID + numLSUTables
        let maxTries = 20;
        let foundFieldIDs = false;
        if (status === "True") {
            for (let triIdx = 0; triIdx < maxTries; triIdx++) {
                let startFieldID = lastCFID + numLSUTables * (triIdx);
                let endFieldID = lastCFID + numLSUTables * (triIdx + 1);

                let fieldListToCheck = [];
                for (let tempIdx = startFieldID; tempIdx < endFieldID; tempIdx++) {
                    fieldListToCheck.push(`CF_${tempIdx}`)
                }

                // check if these fields exists in LS
                let checkCustomFieldExistQuery = `Select ([o9EKG Table].[Field ID].filter(#.Name in {${joinArray(fieldListToCheck)}})) limit 1;`;
                let checkCustomFieldExistResult = cellsetModule.createCellSet(queryModule.select(checkCustomFieldExistQuery))

                let numFieldsTemp = checkCustomFieldExistResult.rowCount;
                if (numFieldsTemp == 0) {
                    foundFieldIDs = true;
                    lastCFID = startFieldID;
                    Log.Info(`Found CF_ID range to use CF_${startFieldID} to CF_${endFieldID - 1}`)
                    break;
                }
            }

            if (foundFieldIDs == false) {
                RuleOutputToUI = { Status: 'Error', Message: 'Max number of try to search FieldIDs Exhausted!' };
                return;
            }
        }

        Log.Info(`Starting execution from CF_ ${lastCFID}`)

        // query to get max(field position) for each table
        let maxFieldPositionQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID] * {Measure.[o9EKG Field Position], Measure.[o9EKG Field Included]});`
        let maxFieldPositionResult = cellsetModule.createCellSet(queryModule.select(maxFieldPositionQuery));

        let tableFieldPositionMax = {};
        let maxFieldPositionLen = maxFieldPositionResult.rowCount;

        for (let fieldPositionIdx = 0; fieldPositionIdx < maxFieldPositionLen; fieldPositionIdx++) {
            let fieldPositionTemp = maxFieldPositionResult.row(fieldPositionIdx).cell(4);
            let tableNameTemp = maxFieldPositionResult.row(fieldPositionIdx).cell(2).Name;

            // if fieldPositionTemp is not integer, make it zero     
            if (fieldPositionTemp != parseInt(fieldPositionTemp)) {
                fieldPositionTemp = 0;
            }

            if (!tableFieldPositionMax.hasOwnProperty(tableNameTemp)) {
                tableFieldPositionMax[tableNameTemp] = 0;
            }

            tableFieldPositionMax[tableNameTemp] = Math.max(tableFieldPositionMax[tableNameTemp], fieldPositionTemp)
        }


        let tenantStripeFieldQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID] * {Measure.[o9EKG Field Name], Measure.[o9EKG Table Included]}).filter(Measure.[o9EKG Field Name] == "o9_ekg_tenant_stripe");`;
        let tenantStripeFieldResult = cellsetModule.createCellSet(queryModule.select(tenantStripeFieldQuery));

        const numStripeCols = tenantStripeFieldResult.rowCount;

        let tableFieldMapping = {};

        for (tableFieldIdx = 0; tableFieldIdx < numStripeCols; tableFieldIdx++) {
            const tableName = tenantStripeFieldResult.row(tableFieldIdx).cell(2).Name;
            const fieldID = tenantStripeFieldResult.row(tableFieldIdx).cell(3).Name;
            const fieldIncluded = tenantStripeFieldResult.row(tableFieldIdx).cell(5);

            if (!tableFieldMapping.hasOwnProperty(tableName)) {
                tableFieldMapping[tableName] = [];
            }

            tableFieldMapping[tableName].push({ fieldID: fieldID, fieldIncluded: fieldIncluded });
        }

        let fieldListToDelete = [];



        for (let i = 0; i < numLSUTables; i++) {
            let tableType = allLSUTablesResult.row(i).cell(2).Name;
            let tableGroup = allLSUTablesResult.row(i).cell(3).Name;
            let tableName = allLSUTablesResult.row(i).cell(4).Name;

            let tableIncluded = allLSUTablesResult.row(i).cell(5);
            let subBlock = allLSUTablesResult.row(i).cell(6);
            let capibility = allLSUTablesResult.row(i).cell(7);

            if (tableIncluded != 1) {
                tableIncluded = -1;
            }


            if (subBlock == null || capibility == null) {
                continue;
            }


            if (status === "True") {
                if (MT_Status == "true") {
                    RuleOutputToUI = { Status: 'Error', Message: 'Multi Tenant is already enabled!' };
                    return;
                }
                let cf_id = "CF_" + (++lastCFID);

                let createFieldQuery = `createmember( [o9EKG Table].[Table Type] = { , "${tableType}" }  , [o9EKG Table].[Table Group] = { , "${tableGroup}" } , [o9EKG Table].[Table Name] = { , "${tableName}" }, [o9EKG Table].[Field ID] = { ,"${cf_id}"} );`;
                queryModule.update(createFieldQuery);

                let ExecuteIBPLCreateField = `exec procedure EKG_AddFields_InBulk {  "FieldList" : ["${cf_id}"] ,  "Project" : "${project}" , "Blocklist" : [${filterclause(subBlock)}], "Caplist" : [${filterclause(capibility)}],  "user" : "${user}",  "now" : "${now}"  } ;`

                queryModule.update(ExecuteIBPLCreateField);

                let fieldPosition = tableFieldPositionMax[tableName];

                if (fieldPosition != parseInt(fieldPosition)) {
                    fieldPosition = 0;
                }


                let updatefieldproperties = `
                scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Field ID].[${cf_id}]);
                    Measure.[o9EKG Field Name] = "${stripe_col_name}";
                    Measure.[o9EKG Field Short Description] = "Field for stripe column";
                    Measure.[o9EKG Field Data Type] = "String";
                    Measure.[o9EKG Field Included] = "${tableIncluded}";
                    Measure.[o9EKG Field Position] = "${fieldPosition + 1}";
                end scope;`

                queryModule.update(updatefieldproperties);

                let updateSourceFieldMapping = `
                scope: ( &CWV * [o9EKG Table].[Field ID].[${cf_id}] * [o9EKG Customer].[Project Name].[${project}] * [Sequence].[Sequence].[1] ) ;
                Measure.[o9EKG Source Field Mapping] = "Hardcode('')";
                end scope;`
                queryModule.update(updateSourceFieldMapping);

                Log.Info("Completed tenant stripe columns for " + tableName);
            } else {
                // add if not fields are present for this table then skip it
                const tableFieldMap = tableFieldMapping[tableName]
                if (tableFieldMap == undefined) continue;

                for (let fieldIDidx = 0; fieldIDidx < tableFieldMap.length; fieldIDidx++) {
                    let stripeFieldID = tableFieldMap[fieldIDidx]['fieldID']
                    let tableIncludedState = tableFieldMap[fieldIDidx]['fieldIncluded']

                    let queryToPerformAction = ``;
                    if (status === "Include") {
                        if (tableIncludedState == '-1') {
                            continue;
                        }
                        queryToPerformAction = `scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Field ID].[${stripeFieldID}]);
                    Measure.[o9EKG Field Included] = 1;
                    end scope;`;
                    }
                    else if (status === "Exclude") {
                        queryToPerformAction = `scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Field ID].[${stripeFieldID}]);
                    Measure.[o9EKG Field Included] = -1;
                    end scope;`;
                    } else if (status === "False") {
                        fieldListToDelete.push(stripeFieldID)
                        // queryToPerformAction = `deletemember([o9EKG Table].[Field ID] = {, "${stripeFieldID}"});`;

                    }
                    if (queryToPerformAction) {
                        queryModule.update(queryToPerformAction);
                    }
                }

            }

        }

        // handle the status false scenario

        Log.Info(`${status}`)
        if (status === "False") {
            if (MT_Status == "false") {
                RuleOutputToUI = { Status: 'Error', Message: 'Multi Tenant is already disabled!' };
                return;
            }
            let finalFieldListToDelete = chunkArray(fieldListToDelete, 50);
            Log.Info(`${finalFieldListToDelete.length}`)
            for (let idx = 0; idx < finalFieldListToDelete.length; idx++) {

                queryToPerformAction =
                    `DELETE DATA for MODEL [o9EKG Field Validations] Where {&CWV , [o9EKG Customer].[Project Name].[${project}] , [o9EKG Table].[Field ID].filter(#.Name in {${joinArray(finalFieldListToDelete[idx])}}) };
                DELETE DATA for MODEL [o9EKG Field Properties] Where {&CWV , [o9EKG Customer].[Project Name].[${project}] , [o9EKG Table].[Field ID].filter(#.Name in {${joinArray(finalFieldListToDelete[idx])}}) };
                DELETE DATA for MODEL [o9EKG Field Mappings] Where {&CWV , [o9EKG Customer].[Project Name].[${project}] , [o9EKG Table].[Field ID].filter(#.Name in {${joinArray(finalFieldListToDelete[idx])}}) };`;

                queryModule.update(queryToPerformAction);
            }

            // let purgeMemberQuery = `purge members();`;
            // queryModule.update(purgeMemberQuery);
        }

        let updateStatusQuery = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${project}]);
        Measure.[o9EKG Multi Tenant Setup] = "${status}";
        end scope;`;
        let updateStatusQuery2 = `scope: (&CWV * [o9EKG Customer].[Project Name].[${project}]);
        Measure.[o9EKG Multi Tenant Setup] = "${status}";
        end scope;`;
        queryModule.update(updateStatusQuery);
        queryModule.update(updateStatusQuery2);

        let MT_Status_Text = '';
        if (MT_Status != "true") {
            MT_Status_Text = "Enabled"
        }
        else {
            MT_Status_Text = "Disabled"
        }

        RuleOutputToUI = { Status: 'Success', Message: `Successully ${MT_Status_Text} Multi - Tenant Feature!` };
        return;

    };

    var filterclause = function (obj) {
        obj = obj.split(",")
        if (Array.isArray(obj)) {
            return obj.map(function (p) {
                return '"' + p + '"';
            }).join(',');
        }
        return '"' + obj + '"';
    };

    return {
        toggleMultiTenant: toggleMultiTenant
    };
})

