define('o9.EKG.populateRuleExpression', ['o9/data/query', 'o9/data/cellset'], function () {

    let logicalOperators = ["AND", "OR", "NOT"]
    // let comparisonOperators = ["=", "!="]
    let comparisonOperators = ["!=", "=", ">", "<", "BETWEEN", " IN ", " LIKE ", " IS NOT NULL ", " IS NULL "]


    var filterDimAttr = function (ruleExpression) {
        let ruleParts = [ruleExpression];
        for (let operator of logicalOperators) {
            let tempRuleParts = [];
            for (let ruleExp of ruleParts) {
                let isIncluded = ruleExp.toLowerCase().includes(operator.toLowerCase())
                if (isIncluded) {
                    let caseInsensitiveDelimiter = new RegExp(operator, 'i');
                    let rulePartsTemp = ruleExp.split(caseInsensitiveDelimiter);
                    tempRuleParts.push(...rulePartsTemp)
                }
                else {
                    tempRuleParts.push(ruleExp)
                }
            }
            ruleParts = tempRuleParts;
        }

        // Step 2: Extract dimension and attribute names for validation
        let fields = [];

        ruleParts.forEach(part => {
            Log.Info(part);
            // trim the parts
            part = part.replace('[', '').replace(']', '')
            part = part.replace('(', '').replace(')', '')

            if (part.includes('.')) {
                let [dim, attr] = part.split('.'); // Assuming dimension is before the dot, attribute is after
                let val = '';
                for (let op of comparisonOperators) {
                    if (attr.includes(op)) {
                        attr = attr.split(op);
                        val = attr.length > 0 ? attr[1] : '';
                        attr = attr[0];
                    }
                }
                if (dim) {
                    dim = dim.trim();
                    attr = attr.trim();
                }
                fields.push({ dim: dim, attr: attr, val: val });
            }
        });

        Log.Info(fields)

        return fields;
    }

    /** this function will take rule expression and perform following steps
     *  1. Divide the expression by Logical Operators
     *  2. Make sure each dim name are same
     *  3. This Dim should not be present in any other rule for this rule set
    **/

    var validateRuleExpression = function (ruleExpression, existingRuleExpression) {
        // Step 1: Split the rule expression into dim-attr
        let fields = filterDimAttr(ruleExpression)

        if (fields.length == 0) {
            return "No Valid field Name found";
        }

        let dimName = fields[0]['dim'];
        for (let dim of fields) {
            Log.Info(`${dim['dim']}, ${dimName}`)
            if (dimName != dim['dim']) {
                return "Only 1 Dimension allowed per rule expression";
            }
        }

        for (let existingRule of existingRuleExpression) {
            let existingDimensions = filterDimAttr(existingRule)

            for (let existingDimAttr of existingDimensions) {
                if (existingDimAttr['dim'] == dimName) {
                    return "Dimension is already used in another rule";
                }
            }
        }

        return "Valid";
    };

    var populateRuleExpression = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const Project = parsedParams.Project;
        const Rule = parsedParams.Rule;
        const RuleSet = parsedParams.RuleSet;
        const RuleType = parsedParams.RuleType;
        let Expression = parsedParams.Expression;

        Expression = Expression.replace(/"/g, "'");

        Log.Info("Started populateRuleExpression Plugin");

        let ruleSetRuleCountQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Rule Set].[Rule Set] * [o9EKG Rule Set].[Rule].[${RuleSet} - ${Rule}] * {Measure.[o9EKG Rule Expression]});`;
        let ruleSetRuleCount = cellsetModule.createCellSet(queryModule.select(ruleSetRuleCountQuery));
        let ruleSetRuleCountLength = ruleSetRuleCount.rowCount;
        if (ruleSetRuleCountLength > 0) {
            let parentRuleSet = ruleSetRuleCount.row(0).cell(2).Name;
            RuleOutputToUI = { Status: 'Error', Message: `Rule is already present in Ruleset: ${parentRuleSet}!` };
            return;
        }

        let ruleSetRuleQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Rule Set].[Rule Set].[${RuleSet}] * [o9EKG Rule Set].[Rule] * {Measure.[o9EKG Rule Set Description], Measure.[o9EKG Rule Expression], Measure.[o9EKG Rule Type]}).filter(~isNull(Measure.[o9EKG Rule Set Description]));`;
        let ruleSetRuleResult = cellsetModule.createCellSet(queryModule.select(ruleSetRuleQuery));
        let ruleSetRuleLength = ruleSetRuleResult.rowCount;

        let existingRuleExpression = [];
        for (let ruleExpIdx = 0; ruleExpIdx < ruleSetRuleLength; ruleExpIdx++) {
            let ruleExp = ruleSetRuleResult.row(ruleExpIdx).cell(5);
            let ruleType = ruleSetRuleResult.row(ruleExpIdx).cell(6);

            if (ruleType == "Cascade") {
                existingRuleExpression.push(ruleExp)
            }

        }

        let validationResult = validateRuleExpression(Expression, existingRuleExpression)

        if (validationResult != "Valid") {
            RuleOutputToUI = { Status: 'Error', Message: validationResult };
            return;
        }


        let createRuleSetMemberQuery = `createmember([o9EKG Rule Set].[Rule Set] = {, "${RuleSet}"}, [o9EKG Rule Set].[Rule] = {, "${RuleSet} - ${Rule}"} );`
        let populateRuleSetPropertiesQuery = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Rule Set].[Rule].[${RuleSet} - ${Rule}]);
Measure.[o9EKG Rule Type] = "${RuleType}";
Measure.[o9EKG Rule Expression] = "${Expression}";
end scope;`

        Log.Info(createRuleSetMemberQuery)
        queryModule.update(createRuleSetMemberQuery)

        Log.Info(populateRuleSetPropertiesQuery)
        queryModule.update(populateRuleSetPropertiesQuery)

        RuleOutputToUI = { Status: 'Success', Message: 'New Rule Expression added!' };
        return RuleOutputToUI
    };


    var editRuleExpression = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const Project = parsedParams.Project;
        const RuleSet = parsedParams.RuleSet;
        const Rule = parsedParams.Rule;
        const RuleType = parsedParams.RuleType;
        const OldRuleExpression = parsedParams.OldRuleExpression;
        let Expression = parsedParams.RuleExpression;


        // check if there are any changes:
        if (OldRuleExpression == Expression) {
            RuleOutputToUI = { Status: 'Success', Message: 'Rule Expression Updated!' };
            return RuleOutputToUI
        }

        Expression = Expression.replace(/"/g, "'");

        // get the dim name from old expression
        let oldFields = filterDimAttr(OldRuleExpression)

        // get the fields from new expression
        let newFields = filterDimAttr(Expression);

        // old and new dims should be same
        if (!(oldFields.length > 0 && newFields.length > 0 && oldFields[0]['dim'] == newFields[0]['dim'])) {
            RuleOutputToUI = { Status: 'Error', Message: 'Dimension Name of fields can not be changed' };
            return;
        }

        let ruleSetRuleCountQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Rule Set].[Rule Set].[${RuleSet}] * [o9EKG Rule Set].[Rule] * {Measure.[o9EKG Rule Set Description], Measure.[o9EKG Rule Expression], Measure.[o9EKG Rule Type]}).filter(~isNull(Measure.[o9EKG Rule Set Description]));`;
        let ruleSetRuleCountResult = cellsetModule.createCellSet(queryModule.select(ruleSetRuleCountQuery));
        let ruleSetRuleCountLength = ruleSetRuleCountResult.rowCount;

        let existingRuleExpression = [];

        for (let ruleExpIdx = 0; ruleExpIdx < ruleSetRuleCountLength; ruleExpIdx++) {
            let ruleExp = ruleSetRuleCountResult.row(ruleExpIdx).cell(5);
            let ruleType = ruleSetRuleCountResult.row(ruleExpIdx).cell(6);

            if (ruleType == "Cascade" && ruleExp != OldRuleExpression) {
                existingRuleExpression.push(ruleExp)
            }
        }

        let validationResult = validateRuleExpression(Expression, existingRuleExpression)

        if (validationResult != "Valid") {
            RuleOutputToUI = { Status: 'Error', Message: validationResult };
            return;
        }


        let populateRuleSetPropertiesQuery = `scope: (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Rule Set].[Rule].[${Rule}]);
            Measure.[o9EKG Rule Expression] = "${Expression}";
        end scope;`

        Log.Info(populateRuleSetPropertiesQuery)
        queryModule.update(populateRuleSetPropertiesQuery)

        RuleOutputToUI = { Status: 'Success', Message: 'New Rule Expression added!' };
        return RuleOutputToUI

    }

    return {
        populateRuleExpression: populateRuleExpression,
        editRuleExpression: editRuleExpression
    };
})

