define('o9.ekgconfig.dict', ['o9/data/query', 'o9/data/cellset'], function() {
    var dictionary = function(o9Params) {

        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);
        Log.Info('o9 params ---' + o9Params);

        var escap = parsedParams.Escape;
        var quot = parsedParams.Quote;
        var encoding = parsedParams.Encoding;
        var multiline = parsedParams.MultiLineJSON;

        // Escape handling
        if (escap === '"') {
            escap = '\\\"';
        } else if (escap === "'") {
            escap = "\\\\\'";
        } else if(escap === "\\"){
            escap='\\\\\\';
        }else if (escap === undefined || escap === null || escap === '') {
            escap = undefined; // Don't set if not provided or invalid
        }

        // Quote handling
        if (quot === '"') {
            quot = '\\\"';
        } else if (quot === "'") {
            quot = "\\\\\'";
        } else if (quot === undefined || quot === null || quot === '') {
            quot = undefined; // Don't set if not provided or invalid
        }

        // Encoding handling
        if (encoding === undefined || encoding === null || encoding === '') {
            encoding = undefined; // Don't set if not provided
        }

        // MultiLineJSON handling (only include if it has a value)
        if (multiline === undefined || multiline === null || multiline === '') {
            multiline = undefined; // Don't set if not provided
        }

        Log.Info('o9 params ---' + escap);
        Log.Info('o9 params ---' + quot);
        Log.Info('o9 params ---' + encoding);
        Log.Info('o9 params ---' + multiline);

        // Check if all parameters are NULL or undefined
        if (!escap && !quot && !encoding && !multiline) {
            Log.Info('o9 params --- All parameters are NULL, skipping dictionary construction');
            return;  // Skip dictionary construction if all are NULL or undefined
        }

        // Constructing the dictionary based on conditions
        let dict = {};
        if (encoding) dict['encoding'] = encoding.toString();
        if (quot) dict['quote'] = quot;
        if (escap) dict['escape'] = escap;
        if (multiline !== undefined) dict['multiline'] = multiline.toString(); // Only add if multiLine is defined

        // Check if dict is empty
        if (Object.keys(dict).length === 0) {
            Log.Info('o9 params --- Empty dictionary, skipping dictionary construction');
            // Skip constructing dictString and don't set ScopeQuery
            return;
        }

        // Manually constructing the dictionary string
        let dictString = '{';
        for (let key in dict) {
            if (dict.hasOwnProperty(key)) {
                // Manually format the key-value pairs (escape quotes properly)
                dictString += `'${key}': '${dict[key]}', `;
            }
        }
        // Remove the trailing comma and space
        dictString = dictString.slice(0, -2) + '}';

        Log.Info('o9 params ---' + dictString); // Log the manually constructed dictionary string

        // Constructing the ScopeQuery with the manually constructed dictString
        var ScopeQuery = `cartesian scope: (&CWV *[o9EKG Customer].[Project Name].[` + parsedParams.Project + `] * [o9EKG Documents].[File Name].[` + parsedParams.SelectedFile + `] *[o9EKG Table].[Table Name].[` + parsedParams.Table + `]);Measure.[o9EKG Text Qualifier] ="` + dictString + `";end scope;`;

        Log.Info("<EKG - Create Dictionary > :" + ScopeQuery);

        queryModule.update(ScopeQuery);

    };
    return {
        dictionary: dictionary
    };
});

