            if (pipelineGroupLength > 128) {
                RuleOutToUI += "Maximum Allowed Length is 128"
            }

            if (RuleOutToUI) {
                RuleOutputToUI = { Status: 'Error', Message: RuleOutToUI };
            }

        }

        return "";
    }

    return {
        validatePipelineGroup: validatePipelineGroup
    };
})

