define('o9.EKG.MassValidationJS', ['o9/data/query', 'o9/data/cellset'], function () {

    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');
    var errorNumber = 0;
    const ignoreErrorMap = new Map();
    const ignoresequenceList = [];
    const TableList = new Set();
    const FieldList = new Set();
    const IncludedOutTableList = new Set();
    const IncludedLSDTableList = new Set();
    const IncludedLSUTableList = new Set();
    const FieldDetailsMap = new Map();
    const tableSourceFieldMap = new Map();
    const navigationMapping = new Map();
    const validationMapping = new Map();
    const multilevelMap = new Map();
    const tableuniqueGroupby = new Map(); 
    const tableuniqueGroupbytext = new Map(); 
    let Project ;
    let version ;

    function isFieldNamePresent(tableName, fieldName) {
        if (!FieldDetailsMap.has(tableName)) {
            return false; 
        }
        return FieldDetailsMap.get(tableName).some(field => field[0] === fieldName);
    };

    var ExcuteIBPL = function(validationNavigation,errorText,validationCategory,navigationFilters,validationCriticality,validationEntity){
        let temnpErrorList = JSON.stringify([validationNavigation,errorText,validationCategory,navigationFilters,validationCriticality,validationEntity]);
        if(!ignoreErrorMap.has(temnpErrorList)){
            errorNumber+=1;
            let queryCreateMassValidation = `cartesian scope : ([Version].[Version Name].[${version}] * [o9EKGSequence].[o9EKGSequenceNumber].[${errorNumber}] * [o9EKG Customer].[Project Name].[${Project}]) ;
                Measure.[o9EKG Error Text] ="${errorText}";
                Measure.[o9EKG Navigation Filters] ="${navigationFilters}";
                Measure.[o9EKG Validation Category] ="${validationCategory}";
                Measure.[o9EKG Validation Criticality] ="${validationCriticality}";
                Measure.[o9EKG Validation Entity] ="${validationEntity}";
                Measure.[o9EKG Validation Navigation] ="${validationNavigation}";
                end scope;`;                  
    
            var queryCreateMassValidationResponse = queryModule.update(queryCreateMassValidation);
        }
    };

    var ValidateFieldname = function () {
        Log.Info(`<MassValidation>-Started Validate Field name with ${Project} , ${version} `);
        let fieldListQuery = `Select ([Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID] * {Measure.[o9EKG Date Time Format], Measure.[o9EKG Field Created By], Measure.[o9EKG Field Created Source Project], Measure.[o9EKG Field Creation Timestamp], Measure.[o9EKG Field Data Type], Measure.[o9EKG Field Default Value], Measure.[o9EKG Field Description], Measure.[o9EKG Field Included], Measure.[o9EKG Field IsKey], Measure.[o9EKG Field Locked], Measure.[o9EKG Field Name], Measure.[o9EKG Field Placeholder 1], Measure.[o9EKG Field Placeholder 2], Measure.[o9EKG Field Placeholder 3], Measure.[o9EKG Field Placeholder 4], Measure.[o9EKG Field Placeholder 5], Measure.[o9EKG Field Position], Measure.[o9EKG Field Short Description], Measure.[o9EKG Is Mapped],Measure.[o9EKG LS Dim Check],Measure.[o9EKG Input Master Data Check]}).filter(Measure.[o9EKG Field Included]==1);`;
        let fieldListResult = cellsetModule.createCellSet(queryModule.select(fieldListQuery));            
        let fieldNameMap = new Map();
        let valid_types = ["Datetime", "Boolean", "Integer", "String", "Number"];  
        const invalidCharacters = new Set(['~', '!', '@', '#', '$', '%', '^', '&', '*', '<', '>', '?', '/', '|', '"', "'", ':', '\\']);
        for(let index = 0; index < fieldListResult.rowCount ; index++){
            let tableName = fieldListResult.row(index).cell(2).Name;
            let fieldId = fieldListResult.row(index).cell(3).Name;
            let fieldDatatype = fieldListResult.row(index).cell(8);
            let fieldDescription = fieldListResult.row(index).cell(10);
            let fieldkey = fieldListResult.row(index).cell(12) ;
            let fieldName = fieldListResult.row(index).cell(14) ;
            let fieldposition = fieldListResult.row(index).cell(20);
            let InputMasterDataCheck = fieldListResult.row(index).cell(24);
            let lsDIMCheck = fieldListResult.row(index).cell(23);
            let tempAppendList = [fieldName, fieldId, fieldposition, fieldkey]; 
            TableList.add(tableName);
            (FieldDetailsMap.get(tableName) || FieldDetailsMap.set(tableName, []).get(tableName)).push(tempAppendList);            
            if(fieldName != null)FieldList.add(fieldName);

            if(lsDIMCheck !== "" && lsDIMCheck !== '' && !IncludedLSDTableList.has(tableName) && !IncludedOutTableList.has(tableName) && !IncludedLSUTableList.has(tableName)){
                if ((lsDIMCheck  && !lsDIMCheck.includes(".") ) ) {
                    let validationNavigation = navigationMapping.get("LS Dim Check")[0];
                    let validationEntity = tableName;
                    let validationCriticality = "Error";
                    let validationCategory = validationMapping.get("LS Dim Check");              
                    let navigationFilters = navigationMapping.get("LS Dim Check")[1];
                    let errorText = `The Field Name '${fieldName}' has the LS Dim Check value set as '${lsDIMCheck}'.The expected LS Dim Check format is [DimensionName].[AttributeName].`;
                    ExcuteIBPL(validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity);
