                        ExcuteIBPL(validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project);
                    }   
                    for (let matchtable in matching_tables) {
                        let pipelineTargetValue = (gcListMap.has(matchtable) && gcListMap.get(matchtable)[0] != null) ? gcListMap.get(matchtable)[0] : "";
                        let syncDirectionValue = (gcListMap.has(matchtable) && gcListMap.get(matchtable)[1] != null) ? gcListMap.get(matchtable)[1] : "" ;
                        if(pipelineTargetValue !="Tenant" || syncDirectionValue != "External"){

                            let updateGCSettings = `scope: ([Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Name].[${matching_tables[matchtable]}]*[o9EKG Table].[Table Type].[LS_Upload]);
                                Measure.[o9EKG LS GC Sync Direction]  = "External" ;
                                Measure.[o9EKG LS GC Pipeline Target] = "Tenant";
                                end scope;`;
                        
                            var updateGCSettingsResponse = queryModule.update(updateGCSettings);

                            // Append this in Error report:-
                            let validationNavigation = navigationMapping.get("GC Pipeline Target")[0];
                            let validationEntity = tableName;
                            let validationCriticality = "Info";
                            let validationCategory = validationMapping.get("GC Pipeline Target");             
                            let navigationFilters = navigationMapping.get("GC Pipeline Target")[1];
                            let errorText = `For table '${tableName}', the GC Pipeline Target setting is set to '${pipelineTargetValue}'. For this table, the GC Pipeline Target setting has been auto-corrected to 'Tenant' and the GC Sync Direction to 'External'.`;
                            //ExcuteIBPL(validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project);

                            uniqueInfoMap.set(tableName, { 
                                validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project 
                            });                            
                        }                                              
 
                    }
                }
            }
        }

        // Append Info //
        for (let [tableName, errorDetails] of uniqueInfoMap) {
            ExcuteIBPL(
                errorDetails.validationNavigation, 
                errorDetails.errorText, 
                errorDetails.validationCategory, 
                errorDetails.navigationFilters, 
