                errorDetails.validationEntity, 
                errorDetails.version, 
                errorDetails.Project
            );
        }
    };

    function extractColumnsFromClause(clause) {
        clause = clause.replace(/'[^']*'/g, '');
        const columnPattern = /`([^`]+)`|([a-zA-Z_]\w*(?:[^\s(),;{}=\n\t]*)?)/g;
        const sqlKeywords = new Set([
            'cast', 'as', 'or', 'and', 'between', 'to_date', 'double', 'select', 'from', 
            'where', 'is', 'not', 'null', 'inner', 'outer', 'join', 'left', 'right', 'union', 
            'group', 'order', 'limit', 'having', 'distinct', 'in', 'like', 'exists', 'case',
            'when', 'then', 'end', 'else', 'coalesce', 'count', 'avg', 'sum', 'min', 'max',
            'date', 'time', 'timestamp', 'now', 'current_date', 'current_time', 'date_add',
            'date_sub', 'trim', 'substring', 'concat', 'concat_ws', 'abs', 'round', 'ceil',
            'floor', 'coalesce', 'if', 'isnull', 'nvl', 'decode', 'contains', 'select',
            'from', 'where', 'exists', 'by', 'desc', 'asc'
        ]);
        
        const columns = new Set();
        let match;
        
        while ((match = columnPattern.exec(clause)) !== null) {
            let columnName = match[1] || match[2];
            
            if (columnName && !sqlKeywords.has(columnName.toLowerCase())) {
                columns.add(columnName);
            }
        }
        
        return Array.from(columns);
    };
    
