    function extractMutitenantTableNames(inputString) {
        let pattern = /Cascaded from:\s*([\w,\s]+)/;
        let match = inputString.match(pattern);
        if (match) {
            let tableNames = match[1];
            return tableNames.split(',').map(name => name.trim());
        }
    
        return [];
    };
    
    function extractParts(expression) {
        let tokens = [];
        let current = "";
        let inBrackets = false;
        let inSingleQuotes = false;
        let inDoubleQuotes = false;
        const operators = new Set(['=', '!=', '<', '>', '<=', '>=']); 
        let i = 0;
        while (i < expression.length) {
            let char = expression[i];
    
            if (char === '[') {
                inBrackets = true;
            } else if (char === ']') {
                inBrackets = false;
            } else if (char === "'") {
                inSingleQuotes = !inSingleQuotes;
            } else if (char === '"') {
                inDoubleQuotes = !inDoubleQuotes;
            }
    
            // If an operator is found outside quotes and brackets, split it
            if ((char === '=' || char === '!' || char === '<' || char === '>') && !(inBrackets || inSingleQuotes || inDoubleQuotes)) {
                if (current) {
                    tokens.push(current);
                    current = "";
                }
                // Handle multi-character operators like !=, >=, <=
                if (i + 1 < expression.length && operators.has(expression.substring(i, i + 2))) {
                    tokens.push(expression.substring(i, i + 2));
                    i++; // Skip next character as it's part of the operator
                } else {
                    tokens.push(char);
                }
            } else if (char === ' ' && !(inBrackets || inSingleQuotes || inDoubleQuotes)) {
                if (current) {
                    tokens.push(current);
                    current = "";
                }
            } else {
                current += char;
            }
    
            i++;
        }
    
        if (current) {
            tokens.push(current);
        }
    
        return tokens;
    };

    function extractFirstValues(expression) {
        const operators = new Set(['=', '!=', '<', '>', '<=', '>=']);
        
        return expression.split(/\s+(AND|OR)\s+/i)
            .map(condition => {
                const match = condition.match(/(.+?)\s*(=|!=|<|>|<=|>=)/);
                return match ? match[1].trim() : null;
            })
            .filter(Boolean);
    }

    function extractBracketedValues(str) {
        const matches = str.match(/\[(.*?)\]/g);
        if (!matches) return [null, null]; 
        const values = matches.map(s => s.replace(/\[|\]/g, ''));
        return [values[0] || null, values[1] || null]; 
    };

    function validateLSUString(s) {
        // Regular expression to match the pattern '[text].[text]'
        const pattern = /^\[.*\]\.\[.*\]$/;
        return pattern.test(s);
    };

    function allSubstringsHaveValidOperator(s) {
        const allowedOperators = new Set(['=', '!=', '<', '>', '<=', '>=']);
        const substrings = s.split(/\s+AND\s+|\s+OR\s+/);
        return substrings.every(substring => {
            const foundOperators = substring.match(/!=|<=|>=|=|<|>/g) || [];
            return foundOperators.some(op => allowedOperators.has(op));
        });
    }

