                                errorList.push({ errorText, tableName, var1 });
                            }
                        }
                    }
                }
            }
        }
        
        const uniqueErrors = new Map();
        (errorList || []).forEach(({ errorText, tableName, var1 }) => {
            // Create a unique key based on relevant properties
            let key = `${errorText}|${tableName}|${var1}`;

            // Add to map if the key doesn't exist
            if (!uniqueErrors.has(key)) {
                uniqueErrors.set(key, { errorText, tableName, var1 });
