    var GCPipelineTarget = function () {

        Log.Info(`<MassValidation>-Started Validate GC Pipeline Target with ${Project} , ${version}  , ${validationMapping} and ${navigationMapping}`);
      
        let tableListQuery = `select ([Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}]*[o9EKG Table].[Table Name] * {measure.[o9EKG Table Included],Measure.[o9EKG LS GC Pipeline Target],Measure.[o9EKG LS GC Sync Direction],Measure.[o9EKG LS Table Type],Measure.[o9EKG LS Dimension Name]} ).filter( measure.[o9EKG Table Included] == 1 );`;
        let tableListResult = cellsetModule.createCellSet(queryModule.select(tableListQuery));
