        // pipeline - Table Mappings
        let tablePipelineMapQuery = `Select ([o9EKG Batch Type].[Batch Type] * [o9EKG Pipeline].[Pipeline Name] * [Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}]  * {Measure.[o9EKG Table Order], Measure.[o9EKG Pipeline Folder Group]});`;
