            if(!tablePipelinehashMap.has(tableName)){tablePipelinehashMap.set(tableName, []);}
            tablePipelinehashMap.get(tableName).push(pipelienName);
        }

        // GC List
        const gcListMap = new Map();
        let lsuGCTablequery = `select ([Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}]* [o9EKG Table].[Table Name] * {measure.[o9EKG Table Included],Measure.[o9EKG LS GC Pipeline Target],Measure.[o9EKG LS GC Sync Direction]} );`;
        let lsuGCTablequeryResult = cellsetModule.createCellSet(queryModule.select(lsuGCTablequery));
        for (let row = 0; row < lsuGCTablequeryResult.length; row++) {
            let pipelineTargetObj = lsuGCTablequeryResult.row(row).cell(4);
            let syncDirectionObj = lsuGCTablequeryResult.row(row).cell(5);
            let tableName = lsuGCTablequeryResult.row(row).cell(2).Name;
            if (!gcListMap.has(tableName)) {
                gcListMap.set(tableName, [null,null]);
            }
            let tempLi = [pipelineTargetObj, syncDirectionObj];
            gcListMap.set(tableName, tempLi);
        }

        let uniqueInfoMap = new Map();
        for (let row = 0; row < tableListResult.rowCount; row++) {
            let GCPipelineTarget = tableListResult.row(row).cell(4) ? tableListResult.row(row).cell(4) : "";
            let tableName = tableListResult.row(row).cell(2) ? tableListResult.row(row).cell(2).Name : "";
            let lsTabletype = tableListResult.row(row).cell(6) ? tableListResult.row(row).cell(6): "";

            if(lsTabletype != null && (lsTabletype == "Fact" || lsTabletype == "Graph")){
                if( GCPipelineTarget == "LS External Table Overwrite" || GCPipelineTarget == "LS External Table Merge"){
                    
                    let fieldListResult = (FieldDetailsMap.has(tableName) ? FieldDetailsMap.get(tableName) : []).filter(([fieldName, fieldId, fieldposition, fieldkey]) => fieldkey === true);
                    let dimension_names = [];
                    for (let field = 0; field < fieldListResult.length; field++) {
                        let fieldName = fieldListResult[field][0];
                        let match = fieldName.match(/\[([^\]]+)\]/);
                        if (match) {dimension_names.push(match[1]);}
                    }
                    let matching_tables = [];
                    for (let tableNameobj of IncludedLSUTableList) {
                        if (dimension_names.some(dim => tableNameobj.includes(dim))) {matching_tables.push(tableNameobj);}
                    }
                    let pipelineNames = tablePipelinehashMap.get(tableName) || [];
                    let flag = false;
                    for (let matchtable in matching_tables) {
                        let matchpipeline = tablePipelinehashMap.get(matchtable) || [];

                        if (matchpipeline.some(item => pipelineNames.includes(item))) {
                            flag = true;
                            break;
                        }
                    }
                    if(flag){
                        let validationNavigation = navigationMapping.get("GC Pipeline Target")[0];
                        let validationEntity = tableName;
                        let validationCriticality = "Error";
                        let validationCategory = validationMapping.get("GC Pipeline Target");             
                        let navigationFilters = navigationMapping.get("GC Pipeline Target")[1];
                        let errorText = `For table '${tableName}', the GC Pipeline Target setting is set to LS External Table Overwrite or LS External Table Merge.For pipelines '${pipelineNames}', the '${matching_tables}' table is grouped along with '${dimension_names}'.A fact type table cannot be grouped with dimension type tables for this GC Pipeline Target setting.`;
                        ExcuteIBPL(validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project);
                    }   
                    for (let matchtable in matching_tables) {
                        let pipelineTargetValue = (gcListMap.has(matchtable) && gcListMap.get(matchtable)[0] != null) ? gcListMap.get(matchtable)[0] : "";
                        let syncDirectionValue = (gcListMap.has(matchtable) && gcListMap.get(matchtable)[1] != null) ? gcListMap.get(matchtable)[1] : "" ;
                        if(pipelineTargetValue !="Tenant" || syncDirectionValue != "External"){

                            let updateGCSettings = `scope: ([Version].[Version Name].[${version}] * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Name].[${matching_tables[matchtable]}]*[o9EKG Table].[Table Type].[LS_Upload]);
                                Measure.[o9EKG LS GC Sync Direction]  = "External" ;
                                Measure.[o9EKG LS GC Pipeline Target] = "Tenant";
                                end scope;`;
                        
                            var updateGCSettingsResponse = queryModule.update(updateGCSettings);

                            // Append this in Error report:-
                            let validationNavigation = navigationMapping.get("GC Pipeline Target")[0];
                            let validationEntity = tableName;
                            let validationCriticality = "Info";
                            let validationCategory = validationMapping.get("GC Pipeline Target");             
                            let navigationFilters = navigationMapping.get("GC Pipeline Target")[1];
                            let errorText = `For table '${tableName}', the GC Pipeline Target setting is set to '${pipelineTargetValue}'. For this table, the GC Pipeline Target setting has been auto-corrected to 'Tenant' and the GC Sync Direction to 'External'.`;
                            //ExcuteIBPL(validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project);

                            uniqueInfoMap.set(tableName, { 
                                validationNavigation, errorText, validationCategory, navigationFilters, validationCriticality, validationEntity, version, Project 
                            });                            
                        }                                              
 
                    }
                }
            }
        }

        // Append Info //
        for (let [tableName, errorDetails] of uniqueInfoMap) {
            ExcuteIBPL(
                errorDetails.validationNavigation, 
                errorDetails.errorText, 
                errorDetails.validationCategory, 
                errorDetails.navigationFilters, 
                errorDetails.validationCriticality, 
                errorDetails.validationEntity, 
                errorDetails.version, 
                errorDetails.Project
            );
        }
    };

    function extractColumnsFromClause(clause) {
        clause = clause.replace(/'[^']*'/g, '');
        const columnPattern = /`([^`]+)`|([a-zA-Z_]\w*(?:[^\s(),;{}=\n\t]*)?)/g;
        const sqlKeywords = new Set([
            'cast', 'as', 'or', 'and', 'between', 'to_date', 'double', 'select', 'from', 
            'where', 'is', 'not', 'null', 'inner', 'outer', 'join', 'left', 'right', 'union', 
            'group', 'order', 'limit', 'having', 'distinct', 'in', 'like', 'exists', 'case',
            'when', 'then', 'end', 'else', 'coalesce', 'count', 'avg', 'sum', 'min', 'max',
            'date', 'time', 'timestamp', 'now', 'current_date', 'current_time', 'date_add',
            'date_sub', 'trim', 'substring', 'concat', 'concat_ws', 'abs', 'round', 'ceil',
            'floor', 'coalesce', 'if', 'isnull', 'nvl', 'decode', 'contains', 'select',
            'from', 'where', 'exists', 'by', 'desc', 'asc'
        ]);
        
        const columns = new Set();
        let match;
        
        while ((match = columnPattern.exec(clause)) !== null) {
            let columnName = match[1] || match[2];
            
            if (columnName && !sqlKeywords.has(columnName.toLowerCase())) {
                columns.add(columnName);
            }
        }
        
        return Array.from(columns);
    };
    
