    // JOINS UTILITY FUNCTION
    function buildJoinTableColumnMap(s, result = [], isCrossJoin) {
        if (!s || s.length === 0) return result;
    
        const add = {
            info: [],
            operation: "",
            data: [],
            takenFromFirst: [],
            takenFromSecond: [],
        };
    
        const extractTablesAndFields = (expression) => {
            const map = new Map();
            if (!expression) return map;
    
            let table = "", column = "", insideBraces = false;
            for (let i = 0; i < expression.length; i++) {
                const char = expression[i];
                switch (char) {
                    case ",":
                        if (insideBraces && table) {
                            map.get(table) ? map.get(table).push(column.trim()) : map.set(table, [column.trim()]);
                        }
                        column = "";
                        break;
                    case "{":
                        insideBraces = true;
                        table = table.trim();
                        if (!map.has(table)) map.set(table, []);
                        break;
                    case "}":
                        insideBraces = false;
                        if (table && column) map.get(table) ? map.get(table).push(column.trim()) : map.set(table, [column.trim()]);
                        table = column = "";
                        break;
                    default:
                        if (insideBraces) column += char;
                        else table += char;
                }
            }
            table = table.trim();
            if (table && !map.has(table)) map.set(table, []);
            return map;
        };

        function processValue(value) { value = value.trim();
            if (value.startsWith('`') && value.endsWith('`'))  return value.slice(1, -1);
            if (value.includes('.')) {
              const parts = value.split('.');
              return processValue(parts.slice(1).join('.'));
            }
            return value.replace(/^['"]|['"]$/g, '');
        };
    
        if (s.includes("cross join")) {
            const op = s.lastIndexOf("|");
            const leftPart = s.substring(0, op).trim();
            const crossPart = s.substring(op + 1).trim();
            const prev = buildJoinTableColumnMap(leftPart, result, true);
    
            const a = crossPart.indexOf("("), b = crossPart.indexOf(")");
            const yo = extractTablesAndFields(crossPart.slice(a + 1, b));
    
            let prevTable = prev.length > 0
                ? "IT_Join_" + prev[prev.length - 1].info.join("_")
                : "";
    
            for (const [key, val] of yo.entries()) {
                if (!prevTable) {
                    prevTable = key;
                } else {
                    result.push({
                        info: [prevTable, key],
                        operation: "cross join",
                        data: [],
                        takenFromFirst: prev.length > 0 ? [] : (yo.get(prevTable) ? yo.get(prevTable) : []),
                        takenFromSecond: val,
                    });
                    prevTable = `IT_Join_${prevTable}_${key}`;
                }
            }
    
        } else {
            const parts = s.split("|");
            const baseTable = parts.length > 0 ? parts[0].trim() : "";
            const joinExpr = parts.length > 1 ? parts[1].trim() : "";
            const rawMatches = joinExpr.match(/\{([^}]+)\}/g) || [];
            const matches = rawMatches.map(m => m.slice(1, -1));
            const secondTable = matches[1];
            const leftCols = matches[0] ? matches[0].split(",").map(processValue) : [];
            const rightCols = matches[2] ? matches[2].split(",").map(processValue) : [];
            const extraCols = matches[3] ? matches[3].split(",").map(processValue) : [];
            for (let i = 0; i < leftCols.length; i++) {
                add.data.push([leftCols[i], rightCols[i]]);
            }
    
            add.takenFromFirst.push(...leftCols);
            add.takenFromSecond.push(...rightCols, ...extraCols);
            add.info.push(baseTable, secondTable);
            add.operation = joinExpr.includes("inner join") ? "inner join" : "left join";
    
            result.push(add);
    
            if (parts.length > 2) {
                const remStart = s.indexOf(")");
                const remainder = s.slice(remStart + 1).trim();
                const joinAlias = `IT_Join_${baseTable}_${secondTable}`;
                buildJoinTableColumnMap(`${joinAlias}${remainder}`, result, isCrossJoin);
            }
        }
    
        return result;
    };
  
    // Build table to column map
    function UtilsbuildJoinTableColumnMap(output) {
        const mp = new Map();
        for (const { info, takenFromFirst, takenFromSecond } of output) {
            if (info[0] && !info[0].includes("IT_Join") && !info[0].includes("ODM_Master") && !info[0].includes("Time")  && !info[0].includes("time")) {
            if (!mp.has(info[0])) mp.set(info[0], []);
            mp.get(info[0]).push(...takenFromFirst);
            }
            if (info[1] && !info[1].includes("IT_Join") && !info[1].includes("ODM_Master") && !info[1].includes("Time")  && !info[1].includes("time")) {
            if (!mp.has(info[1])) mp.set(info[1], []);
            mp.get(info[1]).push(...takenFromSecond);
            }
        }
        return mp;
        
    };
    
