        let validationNav = navigationMapping.has("Field Position") ? navigationMapping.get("Field Position") : "";
        let validationCategory = validationMapping.has("Field Position") ? validationMapping.get("Field Position") : "";
        let validationNavigation = validationNav[0];
        let navigationFilters = validationNav[1];
    
        for (let row = 0; row < headerRAWINMappingResult.rowCount; row++) {
            let tableName = headerRAWINMappingResult.row(row).cell(2).Name;
            let fileInsertmed = headerRAWINMappingResult.row(row).cell(5);
    
            if (['Position Based (Ignore Header)', 'Position Based (No Header)'].includes(fileInsertmed)) {
                let fieldListResult = FieldDetailsMap.has(tableName) ? FieldDetailsMap.get(tableName) : [];

                let fieldPositionSet = new Set();
                let errors = [];
                for (let index = 0; index < fieldListResult.length; index++) {
                    let [fieldName, fieldId, fieldposition,fieldkey] = fieldListResult[index];
    
                    if (fieldposition === null || fieldposition === "") {
                        errors.push({
                            errorText: `Field Name '${fieldName}' has a Field Position '${fieldposition}' .The expected Field Position value is a positive number.`,
                            entity: tableName
                        });
                    } else if (!/^\d+$/.test(fieldposition)) {
                        errors.push({
                            errorText: `Field Name '${fieldName}' has a Field Position '${fieldposition}' .The expected Field Position value is a positive number.`,
                            entity: tableName
                        });
                    } else if (fieldposition == 0) {
                        errors.push({
                            errorText: `Field Name '${fieldName}' has a Field Position '${fieldposition}' .The expected Field Position value is a positive number.`,
                            entity: tableName
                        });
                    } else if (fieldPositionSet.has(fieldposition)) {
                        errors.push({
                            errorText: `Field Name '${fieldName}' has a Field Position '${fieldposition}'. Please enter unique positive number for the Field Positions.`,
                            entity: tableName
                        });
                    }
    
                    if (fieldposition !== null) {
                        fieldPositionSet.add(fieldposition);
                    }
                }
    
                // Process all collected errors in one go
                for (let error of errors) {
                    ExcuteIBPL(validationNavigation, error.errorText, validationCategory, navigationFilters, "Error", error.entity, version, Project);
                    
                }
            }
        }
    };

