    function extractParameters2(vlookupFormula) {
        const pattern = /\{([^}]*)\}/g;
        let matches = [];
        let match;
        while ((match = pattern.exec(vlookupFormula)) !== null) {
            matches.push(match[1]);
        }
        return matches;
    };

    function cleanJoinSyntax2(inputString) {
        const parts = inputString.split(/\s+/);
        const cleanedParts = [];
    
        for (let i = 0; i < parts.length; i++) {
            const part = parts[i].toLowerCase();
    
            if (part === "inner" && i + 1 < parts.length && parts[i + 1].toLowerCase() === "join") {
                cleanedParts.push("inner join");
                i++; // skip the next word
            } else if (part === "left" && i + 1 < parts.length && parts[i + 1].toLowerCase() === "join") {
                cleanedParts.push("left join");
                i++; // skip the next word
            } else {
                cleanedParts.push(parts[i].trim());
            }
        }
    
        let cleanedString = cleanedParts.join(" ");
        cleanedString = cleanedString.replace(/ \| /g, "|").replace(/\| /g, "|").replace(/ \|/g, "|");
    
        return cleanedString;
    };

    var optionalLeftJoin = function(){
        Log.Info(`<MassValidation> - Started Optional LeftJoin Check with ${Project} and ${version}`);

        let optionalLeftJoinQuery = `Select ( [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Table].[Table Type] * [o9EKG Table].[Table Group] * [o9EKG Table].[Table Name] * [o9EKG Table].[Field ID] * [Sequence].[Sequence] ) on row,  ({Measure.[o9EKG Field Name], Measure.[o9EKG Source Field Mapping], Measure.[o9EKG SQL Join Anchor Table]}) on column  where {[Version].[Version Name].[${version}]}.filter( Measure.[o9EKG Field Included] == 1 );`;
        let optionalLeftJoinQueryResult = cellsetModule.createCellSet(queryModule.select(optionalLeftJoinQuery));
        const sourceAnchorTableMap = new Map();

        const errorList = [];
        for (let row = 0; row < optionalLeftJoinQueryResult.rowCount; row++) {
            let tableCell3 = optionalLeftJoinQueryResult.row(row).cell(3);
            let tableName = (tableCell3 && tableCell3.Name) ? tableCell3.Name : ""; // Safe access

            let tableCell8 = optionalLeftJoinQueryResult.row(row).cell(8);
            let sourceTableName = (tableCell8 && tableCell8.Name) ? tableCell8.Name : (tableCell8 || ""); 

            sourceTableName = cleanJoinSyntax2(sourceTableName);

            if (!sourceAnchorTableMap.has(tableName)) {
                sourceAnchorTableMap.set(tableName, new Set());
            }
            if (typeof sourceTableName === "string" && (sourceTableName.includes("left join") || sourceTableName.includes("LEFT JOIN"))) { 
                let tables = sourceTableName.split("|");

                for (let i = 1; i < tables.length; i++) {
                    let lookup = tables[i];

                    if (lookup != null && (lookup.startsWith("left join")|| sourceTableName.includes("LEFT JOIN")) && extractParameters2(lookup).length === 4) {
                        let [param1, param2, param3, param4] = extractParameters2(lookup);
                        let fieldList = Array.from(tableSourceFieldMap.get(tableName) || []);
                        let inputFields = Array.from(new Set(param4.split(",")));

                        for (let var1 of inputFields) {
                            let flag = false;
                            for (let var2 of fieldList) {
                                if (var2.includes(var1) && (var2.includes("COALESCE")|| var2.includes("coalesce"))) {
                                    flag = true;
                                    break;
                                }
                            }
                            if (!flag) {
                                let errorText = `The Source Table is '${sourceTableName}' This operation may result in null values. Please consider using the COALESCE function for the '${var1}' field.`;
                                errorList.push({ errorText, tableName, var1 });
                            }
                        }
                    }
                }
            }
        }
        
        const uniqueErrors = new Map();
        (errorList || []).forEach(({ errorText, tableName, var1 }) => {
            // Create a unique key based on relevant properties
            let key = `${errorText}|${tableName}|${var1}`;

            // Add to map if the key doesn't exist
            if (!uniqueErrors.has(key)) {
                uniqueErrors.set(key, { errorText, tableName, var1 });
            }
        });

        // Convert the unique values back to an array and iterate
        for (let { errorText, tableName, var1 } of uniqueErrors.values()) {
            let sourceFieldMapping = navigationMapping.get("Source Field Mapping") || [];
            let validationNavigation = sourceFieldMapping[0] || null;
            let navigationFilters = sourceFieldMapping[1] || null;

            let validationEntity = tableName;
            let validationCriticality = "Error";
            let validationCategory = validationMapping.get("Source Field Mapping") || null;

            if (validationNavigation && validationCategory) {
                ExcuteIBPL(
                    validationNavigation,
                    errorText,
                    validationCategory,
                    navigationFilters,
                    validationCriticality,
                    validationEntity,
                    version,
                    Project
                );
            }
        }           
    };  
        
