        let lsuIncludedtableResult = cellsetModule.createCellSet(queryModule.select(lsuIncludedtable));
        for (let row = 0; row < lsuIncludedtableResult.rowCount; row++) {
            IncludedLSUTableList.add(lsuIncludedtableResult.row(row).cell(2).Name);
        }

        ValidateFieldname();
        DuplicateDataHandling();
        HierarchyCheck();
        GCPipelineTarget();
        RAWINValidation();
        file_insert_methodology_validation();
        tenant_Data_Download();
        oUTRawValidation();
        outBoundsFieldsPosition();
        lsDimCheck();
        fieldLevelMappingValidation();
        optionalLeftJoin();
        multiTenantValidation();
        lsuFactValidation();
    };
    return {
        MassValidationMainFunction: MassValidationMainFunction
    };
});

