define('o9.EKG.validateFileNameRename', ['o9/data/query', 'o9/data/cellset'], function () {
    var validateFileNameRename = function (o9Params) {
        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        Log.Info('o9 params ---' + o9Params);

        const currentFileNam = parsedParams.SelectedFile;
        const currentProjec = parsedParams.ProjectName;
        const currentFileType = parsedParams.fileType;
        const currentFileTypeLower = currentFileType.toLowerCase();

        let newFileName = parsedParams.NewFileName;
        newFileName = newFileName.replace(/"/g, '\\"');

        Log.Info("Started validateFileNameRename Plugin");

        const newFileNameWithoutExtension = newFileName.substring(0, newFileName.lastIndexOf('.'));
        const newFileExtension = newFileName.substring(newFileName.lastIndexOf('.') + 1).toLowerCase();

        // Fetch all existing file names
        let allFilesQuery = `Select ([o9EKG Customer].[Project Name].[${currentProjec}]*[o9EKG Documents].[File Name]* &CWV *[o9EKG Table].[Table Name] *{Measure.[o9EKG Table Included],Measure.[o9EKG File Type]}).filter(Measure.[o9EKG Table Included]==1 );`;
        let allFilesCellSet = cellsetModule.createCellSet(queryModule.select(allFilesQuery));
        const fileRow = allFilesCellSet.rowCount;

        // Check if the new file name (case-sensitive) with extension (case-insensitive) is already associated with another table
        for (let fileIdx = 0; fileIdx < fileRow; fileIdx++) {
            let existingFileName = allFilesCellSet.row(fileIdx).cell(1).Name;
            const existingFileNameWithoutExtension = existingFileName.substring(0, existingFileName.lastIndexOf('.'));
            const existingFileExtension = existingFileName.substring(existingFileName.lastIndexOf('.') + 1).toLowerCase();

            if (newFileNameWithoutExtension === existingFileNameWithoutExtension && newFileExtension === existingFileExtension) {
                RuleOutputToUI = {
                    Status: 'Error',
                    Message: `Rename Failed: File Name [${newFileName}] is already associated with another table. Please enter a different file name.`
                };
                return RuleOutputToUI;
            }
        }

        if (newFileName.endsWith('.')) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Rename Failed: File name cannot end with a dot.'
            };
            return RuleOutputToUI;
        }

        // Allow .dsv and .csv file extensions if current file type is either csv or dsv
        if (currentFileTypeLower === 'csv' || currentFileTypeLower === 'dsv') {
            if (!(newFileExtension === 'csv' || newFileExtension === 'dsv')) {
                RuleOutputToUI = {
                    Status: 'Error',
                    Message: 'Rename Failed: File type "' + currentFileType + '" should have .csv or .dsv extension.'
                };
                return RuleOutputToUI;
            }
        } else if (currentFileTypeLower !== newFileExtension) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Rename Failed: File type and extension do not match.'
            };
            return RuleOutputToUI;
        }

        let namePart = newFileName.substring(0, newFileName.lastIndexOf('.'));
        if (/^\s*$/.test(namePart)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Rename Failed: File name cannot consist of only spaces.'
            };
            return RuleOutputToUI;
        }

        // Condition: File name cannot contain special characters: \, :, ?, ", <, >, |
        const invalidChars = /[\\:\?\\"<>\|]/;
        if (invalidChars.test(newFileName)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Rename Failed: File name cannot contain these characters "\\ : ? \" < > |".'
            };
            return RuleOutputToUI;
        }

        // If all validations pass
        RuleOutputToUI = {
            Status: 'Success',
            Message: ''
        };
        return RuleOutputToUI;
    };

    // function to rename file 
    var FileRename = function (o9Params) {
        var parsedParams = JSON.parse(o9Params);
        var queryModul = require('o9/data/query');
        var cellsetModul = require('o9/data/cellset');

        const currentFileName = parsedParams.SelectedFile;
        const currentProject = parsedParams.ProjectName;
        const fileKey = parsedParams.Key;
        let newFileName = parsedParams.NewFileName;
        let selectedtable = parsedParams.SelectedTable;

        newFileName = newFileName.replace(/"/g, '\\"');

        Log.Info('o9 params ---' + o9Params);

        let selectedFileQuery = `Select (&CWV * [o9EKG Customer].[Project Name].[${currentProject}]* [o9EKG Table].[Table Name]*[o9EKG Table].[Table Group]*[o9EKG Documents].[File Name].[${currentFileName}]*{Measure.[o9EKG File Type],Measure.[o9EKG File Delimiter],Measure.[o9EKG Text Qualifier],Measure.[o9EKG File Decryption],Measure.[o9EKG File Source Type],Measure.[o9EKG File Insert Methodology],Measure.[o9EKG Table Included],Measure.[o9EKG File Requirement], Measure.[o9EKG File Description]}).filter( Measure.[o9EKG Table Included] == 1);`;

        let allFiles = cellsetModul.createCellSet(queryModul.select(selectedFileQuery));
        Log.Info("Fetching selected row ... " + allFiles);
        const fileRows = allFiles.rowCount;

        for (let fileidx = 0; fileidx < fileRows; fileidx++) {
            let tableName = allFiles.row(fileidx).cell(2).Name;
            let tableGroup = allFiles.row(fileidx).cell(3).Name;
            let fileName = allFiles.row(fileidx).cell(4).Name;
            let fileType = allFiles.row(fileidx).cell(5);
            let fileDelimeter = allFiles.row(fileidx).cell(6);
            let textQualifier = allFiles.row(fileidx).cell(7);
            let fileDecryption = allFiles.row(fileidx).cell(8);
            let fileSourceType = allFiles.row(fileidx).cell(9);
            let fileInsertMethod = allFiles.row(fileidx).cell(10);
            let tableIncluded = allFiles.row(fileidx).cell(11);
            let fileReq = allFiles.row(fileidx).cell(12);
            let filDesc = allFiles.row(fileidx).cell(13);

            // delete earlier intersections
            let deleteEarlierIntersection = `DELETE DATA for MODEL [o9EKG File Table Association] Where { &CWV , [o9EKG Customer].[Project Name].[${currentProject}],[o9EKG Documents].[File Name].[${currentFileName}],[o9EKG Table].[Table Name]};`;
            queryModul.update(deleteEarlierIntersection);

            // update file name query 
            let updateFileNameQuery = `createmember( [o9EKG Documents].[File Type] = { ,"IN"} , [o9EKG Documents].[File Name] = { , "${newFileName}" } );`;
            Log.Info("update FileName Query " + updateFileNameQuery);

            let populateQuer = `cartesian scope: ( &CWV * [o9EKG Customer].[Project Name].[${currentProject}] * [o9EKG Documents].[File Name].[${newFileName}] * [o9EKG Table].[Table Name].[${tableName}]);
                Measure.[o9EKG File Type] = ${fileType ? `"${fileType}"` : null};
                Measure.[o9EKG File Delimiter] = ${fileDelimeter ? `"${fileDelimeter}"` : null};
                Measure.[o9EKG Text Qualifier] = ${textQualifier ? `"${textQualifier}"` : null};
                Measure.[o9EKG File Decryption] = ${fileDecryption ? `"${fileDecryption}"` : null};
                Measure.[o9EKG File Source Type] = ${fileSourceType ? `"${fileSourceType}"` : null};
                Measure.[o9EKG File Insert Methodology] = ${fileInsertMethod ? `"${fileInsertMethod}"` : null};
                Measure.[o9EKG Table Included] = ${tableIncluded ? `"${tableIncluded}"` : null};
                Measure.[o9EKG File Requirement] = ${fileReq ? `"${fileReq}"` : null};
                Measure.[o9EKG File Description] = ${filDesc ? `"${filDesc}"` : null};
            end scope;`;

            Log.Info("updateFileNameQuery" + updateFileNameQuery);
            queryModul.update(updateFileNameQuery);

            Log.Info("populateQuery " + populateQuer);
            queryModul.update(populateQuer);
        }
    };

    return {
        validateFileNameRename: validateFileNameRename,
        FileRename: FileRename
    };
});

