    var validateFileName = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        Log.Info('o9 params ---' + o9Params);

        let fileName = parsedParams.FileName; // Using fileName instead of newFileName
        const fileType = parsedParams.FileType; // Store the FileType in a variable
        const existingFile = parsedParams.AlreadyExistedFile; // If there already mapping exists for table name -> file name 
        const project = parsedParams.Project;
        fileName = fileName.replace(/"/g, '\\"');
        Log.Info("Started validateFileNameAdd Plugin");

        // Condition: Check if an existing file mapping already exists for the table
        if (existingFile !== null) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: `Invalid File Mapping: File Name [${existingFile}] is already associated with the table [${parsedParams.TableName}]. Can't add another file name for the same table.`
            };
            return RuleOutputToUI;
        }

        // Condition: Check if the file name is already associated with another table
        // Extract the file name without the extension and the extension itself
        const fileNameWithoutExtension = fileName.substring(0, fileName.lastIndexOf('.')).toLowerCase();
        const fileExtension = fileName.substring(fileName.lastIndexOf('.') + 1).toLowerCase();

        // Fetch all existing file names
        let allFilesQuery = `Select ([o9EKG Customer].[Project Name].[${project}] * [o9EKG Documents].[File Name]* &CWV *[o9EKG Table].[Table Name] *{Measure.[o9EKG Table Included],Measure.[o9EKG File Type]}).filter(Measure.[o9EKG Table Included]==1 );`;
        let allFilesCellSet = cellsetModule.createCellSet(queryModule.select(allFilesQuery));
