        if (fileName.endsWith('.')) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Invalid File Name: File name cannot end with a dot.'
            };
            return RuleOutputToUI;
        }

        // Condition: File name type and extension should match (case-insensitive)
        // Allow both .csv and .dsv for CSV file type
        if (fileType.toLowerCase() === 'csv' && !['csv', 'dsv'].includes(fileExtension)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Invalid File Type: For CSV file type, only .csv or .dsv extensions are allowed.'
            };
            return RuleOutputToUI;
        } else if (fileType.toLowerCase() !== 'csv' && fileType.toLowerCase() !== fileExtension) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Invalid File Type: File type and extension do not match.'
            };
            return RuleOutputToUI;
        }

        // Condition: File name cannot contain special characters: \, :, ?, ", <, >, |
        const invalidChars = /[\\:\?\"<>\|]/; // Updated the character list as per the request
        if (invalidChars.test(fileName)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Invalid File Name: File name cannot contain these characters "\\ : ? \" < > |".'
            };
            return RuleOutputToUI;
        }

        let namePart = fileName.substring(0, fileName.lastIndexOf('.'));
        if (/^\s*$/.test(namePart)) {
            RuleOutputToUI = {
                Status: 'Error',
                Message: 'Invalid File Name: File name cannot consist of only spaces.'
            };
            return RuleOutputToUI;
        }

        // If all validations pass
        RuleOutputToUI = {
            Status: 'Success',
            Message: ''
        };
        return RuleOutputToUI;
    };

