define('o9.EKG.batchupdate', ['o9/data/query', 'o9/data/cellset'], function() {
    var updatebatch = function(o9Params) {

        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);
        Log.Info('o9 params ---' + o9Params);

        var project = parsedParams.Project;
        var pipeline = parsedParams.Pipeline;
        var newBatch = parsedParams.UpdateBatch;
        const pipelineMap = pipeline.map(PipeLine =>`[${PipeLine}]`).join(',');
        //query to fetch complete selected pipelines table  table 
        let fetchSelectedPipelineTable = `select(&CWV * [o9EKG Customer].[Project Name].[${project}]*[o9EKG Pipeline].[Pipeline Name].filter(#.Name in {${pipelineMap}})* [o9EKG Table].[Table Name] * [o9EKG Batch Type].[Batch Type] * {Measure.[o9EKG Table Order], Measure.[o9EKG Pipeline Folder Group]}).filter(~isNull(Measure.[o9EKG Table Order]));`;
        let selectedpipelinetableResult = cellsetModule.createCellSet(queryModule.select(fetchSelectedPipelineTable));
        const pipelineRows = selectedpipelinetableResult.rowCount;
        Log.Info('row count  ---' + pipelineRows);
        for (let pipelineIdx = 0; pipelineIdx < pipelineRows; pipelineIdx++) {
            
            let pipelineName = selectedpipelinetableResult.row(pipelineIdx).cell(2).Name;
            let tableName= selectedpipelinetableResult.row(pipelineIdx).cell(3).Name;
            let batchType=selectedpipelinetableResult.row(pipelineIdx).cell(4).Name;
            let tableOrder =selectedpipelinetableResult.row(pipelineIdx).cell(5);
            let pipelineFoldergroup=selectedpipelinetableResult.row(pipelineIdx).cell(6);


            let NullifyFolderGroupfunction = `scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipelineName}] * [o9EKG Customer].[Project Name].[${project}]*[o9EKG Batch Type].[Batch Type].[${batchType}]);
                            Measure.[o9EKG Pipeline Folder Group] = null ;
                            end scope;`;

            Log.Info('nullify folder group '+NullifyFolderGroupfunction);
            queryModule.update(NullifyFolderGroupfunction);


            let NullifyTableOrderfunction = `scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipelineName}] * [o9EKG Customer].[Project Name].[${project}]*[o9EKG Batch Type].[Batch Type].[${batchType}]*[o9EKG Table].[Table Name]);
                            Measure.[o9EKG Table Order] = null;
                            end scope;`;

        Log.Info('nullify folder group '+NullifyTableOrderfunction);
        queryModule.update(NullifyTableOrderfunction);
              //delete member 
             
        let deleteEarlierIntersection = `DELETE DATA for MODEL [o9EKG Pipeline Properties],[o9EKG Pipeline Table Association] Where { &CWV , [o9EKG Customer].[Project Name].[${project}], [o9EKG Batch Type].[Batch Type].[${batchType}],[o9EKG Pipeline].[Pipeline Name].filter(#.Name in {${pipelineName}}) ,[o9EKG Table].[Table Name]};`;
        queryModule.update(deleteEarlierIntersection);

        let createbatchTypeMemberQuery = `createmember( [o9EKG Batch Type].[Batch Type] = { , "${newBatch}" } );`;
        let populateQuery = `cartesian scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipelineName}]  * [o9EKG Table].[Table Name].[${tableName}]* [o9EKG Batch Type].[Batch Type].[${newBatch}]* [o9EKG Customer].[Project Name].[${project}] ) ;
                Measure.[o9EKG Table Order] =  "${tableOrder}" ;
                Measure.[o9EKG Pipeline Folder Group] = "${pipelineFoldergroup}" ;
                end scope;`;

       Log.Info("createbatchTypeMemberQuery "+createbatchTypeMemberQuery);
            queryModule.update(createbatchTypeMemberQuery);
            
        Log.Info("populateQuery "+populateQuery);
            queryModule.update(populateQuery);
      
            
        }
                
    };

    return {
        updatebatch : updatebatch       
    };
});

