define('o9.EKG.validateBatchType', ['o9/data/query', 'o9/data/cellset'], function () {

    var validateBatchType = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const project = parsedParams.Project;
        const batchType = parsedParams.BatchType;

        // check for duplicate batch type
        let duplicateBatchTypeQuery = `select (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Batch Type].[Batch Type].[${batchType}] * {Measure.[o9EKG Batch Description], Measure.[o9EKG Batch FTB], Measure.[o9EKG Start Email], Measure.[o9EKG Stop Email], Measure.[o9EKG Tenant Active], Measure.[o9EKG Tenant Inactive]});`;
        let duplicateBatchTypeResult = cellsetModule.createCellSet(queryModule.select(duplicateBatchTypeQuery));

        let numOfBatches = duplicateBatchTypeResult.rowCount;

        if (numOfBatches > 0) {
            // return error, duplicate batch found
            RuleOutputToUI = { Status: 'Error', Message: 'Batch already exist' };
            return RuleOutputToUI
        }

        // check validation on batch type
        const batchNTypeRegex = /^[a-zA-Z0-9_]{1,30}$/;

        const isValid = batchNTypeRegex.test(batchType);

        if (isValid) {
            RuleOutputToUI = { Status: 'Success', Message: 'Batch Type Added Successfully!' };
            return RuleOutputToUI
        }
        else {
            RuleOutputToUI = { Status: 'Error', Message: 'Batch Type should be 30 characters or fewer and only include letters, numbers, and underscores.' };
            return RuleOutputToUI
        }


    }

    return {
        validateBatchType: validateBatchType
    }

})

