define('o9.EKG.addTenantInstance', ['o9/data/query', 'o9/data/cellset'], function () {

    var addTenantInstance = function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');

        const Project = parsedParams.Project;
        const TenantProfile = parsedParams.TenantProfile;
        const TenantInstance = parsedParams.TenantInstance;
        const OrgName = parsedParams.OrgName;

        Log.Info("Started addTenantInstance Plugin");


        // Query to get the last used Tenant ID
        let ExistingTenantIDQuery = `Select ([o9EKGSequence].[o9EKGSequenceNumber] * [o9EKG Tenant].[Profile].[${TenantProfile}].[Tenant] *  {Measure.[o9EKG Central Tenant], Measure.[o9EKG Tenant Org Name]}) where {&CWV, [o9EKG Customer].[Project Name].[${Project}]} orderby [o9EKGSequence].[o9EKGSequenceNumber].Key desc limit 1;`;

        let existingIDResult = cellsetModule.createCellSet(queryModule.select(ExistingTenantIDQuery));

        let ID;
        if (existingIDResult.rowCount > 0) {
            let lastSeqID = existingIDResult.row(0).cell(existingIDResult.memberColumnIndex('o9EKGSequence', 'o9EKGSequenceNumber')).Name

            if (existingIDResult.row(0).cell(existingIDResult.memberColumnIndex('o9EKGSequence', 'o9EKGSequenceNumber')) && lastSeqID) {
                ID = lastSeqID;
                Log.Info(`Seq details are Present--------${ID}`);
            } else {
                ID = 0;
                Log.Info(`Seq Details are not present--------${ID}`);
            }
        } else {
            ID = 0;
        }

        ID = parseInt(ID) + 1;

        let createTenantMemberQuery = `createmember([o9EKG Tenant].[Profile] = {, "${TenantProfile}"}, [o9EKG Tenant].[Tenant] = {, "${TenantInstance}"});`
        let populateTenantPropertiesQuery = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${Project}] * [o9EKG Tenant].[Tenant].[${TenantInstance}] * [o9EKGSequence].[o9EKGSequenceNumber].[${ID}]);
            Measure.[o9EKG Central Tenant] = False;
            Measure.[o9EKG Tenant Org Name] = "${OrgName}";
            end scope;`

        Log.Info(createTenantMemberQuery)
        queryModule.update(createTenantMemberQuery)

        Log.Info(populateTenantPropertiesQuery)
        queryModule.update(populateTenantPropertiesQuery)


        RuleOutputToUI = { Status: 'Success', Message: 'New Tenant added!' };
        return RuleOutputToUI
    };
    return {
        addTenantInstance: addTenantInstance
    };
})

