define('o9.ekgconfig.ignoreValidations', ['o9/data/query', 'o9/data/cellset'], function () {
    var ignoreValidations = function(o9Params){

    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset'); 

    var project = parsedParams.Project;
    var Select = parsedParams.IgnoredRows;
    var lastSequence = parseInt(parsedParams.lastNumber);
    Log.Info("<EKG - Ignore Validation Logs > : Ignoring Mass Validations.");
    if(isNaN(lastSequence))
    {
        Log.Info("<EKG - Ignore Validation Logs > : Initializing from sequence 1.");
        lastSequence = 0;
    }
    var metadataFlag = false;
    var severityFlag = false;

    for(var j=0; j<Select.length;j++)
    {
    
    let validationIBPL = 'Select (&CWV * [o9EKG Customer].[Project Name].['+project+'] * [o9EKG Table].[Table Name].['+Select[j].Table.Value+'] * [o9EKGSequence].[o9EKGSequenceNumber].['+Select[j].Seq.Value+'] ) on row,({Measure.[o9EKG Validation Error Text],Measure.[o9EKG Validation Type],Measure.[o9EKG Validation Severity],Measure.[o9EKG Error Type]}) on column;';
    let validationRow = cellsetModule.createCellSet(queryModule.select(validationIBPL));
    

    // var metadataFlag = false;
    // var severityFlag = false ;
    var errorType = validationRow.row(0).cell(7);

    if (errorType == "Metadata")
    {
        metadataFlag = true;

    }
    else 
    {
    var validationType = validationRow.row(0).cell(5);
    var severity = validationRow.row(0).cell(6);
    if (severity == "Error" )
    
    {
      
        severityFlag = true;
        
    }
    var queryIgnoreTextUpdate1 = 'cartesian scope: (&CWV * [o9EKG Customer].[Project Name].['+project+'] * [o9EKG Table].[Table Name].['+Select[j].Table.Value+'] * [o9EKGSequence].[o9EKGSequenceNumber].['+Select[j].Seq.Value+'] );Measure.[o9EKG Validation Error Text] = null ;Measure.[o9EKG Validation Type] = null;Measure.[o9EKG Error Type] = null; Measure.[o9EKG Validation Severity] = null; end scope;';
  
    queryModule.update(queryIgnoreTextUpdate1);
    
    var errorText =  validationRow.row(0).cell(4); 
    let escapedErrorText = '';
    Select[j].Seq.Value = lastSequence + j+1;
    if ((errorText.toLowerCase()).includes("udf_") || (errorText.toLowerCase()).includes("invalid characters")) {
        escapedErrorText = errorText.toString().replace(/"/g, '\\"').replace(/'/g, "\'");
    }
    else {
        escapedErrorText = errorText;
    }
   
    // var queryIgnoreTextUpdate = 'cartesian scope: (&CWV * [o9EKG Customer].[Project Name].['+project+'] * [o9EKG Table].[Table Name].['+Select[j].Table.Value+'] * [o9EKGSequence].[o9EKGSequenceNumber].['+Select[j].Seq.Value+'] );Measure.[o9EKG Ignored Validation Error Text] = "'+escapedErrorText+'" ;Measure.[o9EKG Ignore Validation Type] = "'+validationType.toString()+'" ;Measure.[o9EKG Ignore Severity] = "'+severity.toString()+'" ;end scope;';
    var queryIgnoreTextUpdate = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Table].[Table Name].[${Select[j].Table.Value}] * [o9EKGSequence].[o9EKGSequenceNumber].[${Select[j].Seq.Value}] );
        Measure.[o9EKG Ignored Validation Error Text] = "${escapedErrorText}";
        Measure.[o9EKG Ignore Validation Type] = "${validationType.toString()}";
        Measure.[o9EKG Ignore Severity] = "${severity.toString()}" ;
    end scope;`

    // var queryIgnoreTextUpdate = 'cartesian scope: (&CWV * [o9EKG Customer].[Project Name].['+project+'] * [o9EKG Table].[Table Name].['+Select[j].Table.Value+'] * [o9EKGSequence].[o9EKGSequenceNumber].['+Select[j].Seq.Value+'] );Measure.[o9EKG Ignored Validation Error Text] = "'+errorText.toString()+'" ;Measure.[o9EKG Ignore Validation Type] = "'+validationType.toString()+'" ;Measure.[o9EKG Ignore Severity] = "'+severity.toString()+'" ;end scope;';
    Log.Info("<EKG - Ignore Validation Logs > : query formed ====== " + queryIgnoreTextUpdate);
    queryModule.update(queryIgnoreTextUpdate);
    }

}
    if (metadataFlag === true || severityFlag === true)
    {
    let errorMessage = '';

    if (metadataFlag === true && severityFlag === true) {
        errorMessage = ("You can not ignore MetaData Errors. Some errors were also ignored, which may cause issues later. Please address them.").fontcolor('red');
    } else if (metadataFlag === true) {
        errorMessage = ("You can not ignore MetaData Errors.").fontcolor('red');
    } else if (severityFlag === true) {
        errorMessage = ("Some errors were ignored, which may cause issues later. Please address them.").fontcolor('red');
    }
            RuleOutputToUI = { Status: 'Success', Message: errorMessage};
           
    }
    Log.Info("<EKG - Ignore Validation Logs > : Execution Complete.");   
 
    
    };
          return {
        ignoreValidations: ignoreValidations
    };
});

