define('o9.EKG.AddFieldsinBulk', ['o9/data/query', 'o9/data/cellset'], function () {

    var AddFieldsinBulk =   function (o9Params) {

        var parsedParams = JSON.parse(o9Params);
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
		Log.Info("Started AddFieldsinBulk Plugin");
		var sequencegenerator=[];
		

//Run the Nextlabel select queries N times where N is a parameter passed from action button filed ID NewFieldsCount. LS will return N unique values (1 for every time) the select query is run. Pass the list of values LS returns as csv list to FID in the procedure below
		for (z = 0;z < parsedParams.NewFieldsCount;z++)
			        {
					var GenerateNextSequence =`select (NextLabel([SrNo_FieldID]));`;
					var GenerateNextSequenceData = queryModule.select(GenerateNextSequence);
					
					Log.Info("New Field ID generated in LS "+ GenerateNextSequenceData.Result+ " will be passed to createmember");
					
		var CreateFieldIDMember = `createmember( [o9EKG Table].[Table Type] = { , "${parsedParams.TableType}" }  , [o9EKG Table].[Table Group] = { , "${parsedParams.TableGroup}" } , [o9EKG Table].[Table Name] = { , "${parsedParams.TableName}" }, [o9EKG Table].[Field ID] = { ,"${ GenerateNextSequenceData.Result}"} );`;

				queryModule.update(CreateFieldIDMember);

					//Change this based on the value format based on the select query	
		            sequencegenerator.push(GenerateNextSequenceData.Result);
		            Log.Info("FieldIDs array list"+  JSON.stringify(sequencegenerator));
			        }
        
		//' + filterclause(StyleColor) + ' pass this inside procedure which sends the data in array format into the procedure . ${parsedParams.Project}
		var ExecuteIBPLCreateField =`exec procedure EKG_AddFields_InBulk {  "FieldList" : [${ filterclause(sequencegenerator)}] ,  "Project" : "${parsedParams.Project}" , "Blocklist" : [${ filterclause(parsedParams.Block)}], "Caplist" : [${ filterclause(parsedParams.Cap)}],  "user" : "${parsedParams.user}",  "now" : "${parsedParams.now}"  } ;`;
	

        queryModule.update(ExecuteIBPLCreateField);
		

        return;
    };
 var filterclause = function(obj) {
        if (Array.isArray(obj)) {
            return obj.map(function(p) {
                return '"' + p + '"';
            }).join(',');
        }
        return '"' + obj + '"';
      };
    return {
        AddFieldsinBulk: AddFieldsinBulk
    };
});

