define('o9.EKG.validateDiff', ['o9/data/query', 'o9/data/cellset'], function () {

  var validateDiff = function(o9Params){

    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');


    // Condition: Field Name should be unique within a table
    var ExistingIls = `Select (&CWV * [o9EKG Customer].[Project Name].[${parsedParams.CurrentProject}] * [o9EKGSequence].[o9EKGSequenceNumber].[${parsedParams.SeqNumber}] * [o9EKG Table].[Table Name] * [Sequence].[Sequence] ) on row, ({Measure.[o9EKG Diff Attribute], Measure.[o9EKG Diff Difference Type]}) on column where {Measure.[o9EKG Diff Difference Type] like "Composite (Check Details)"} ;`;
    Log.Info("PRINT --- " +ExistingIls );
    var temp = cellsetModule.createCellSet(queryModule.select(ExistingIls));
    Log.Info("PRINT ValidateDiff" +temp.rowCount);

    if(temp.rowCount > 0) {
      Log.Info("")
      RuleOutputToUI = {
        Status:'Error',
        Message:'Some composite diff rows have been selected, please unselect them to proceed with the merge actions. To merge the particular details for the composite rows, please unhide the diff types Excluded (hidden) and Removed (hidden) by clearing the pivot filters, then select the exact rows before merging them. '
      };
      return RuleOutputToUI;
    }

    return RuleOutputToUI;
  };

  return {
    validateDiff: validateDiff
  };
});

