    var queryIgnoreTextUpdate1 = 'cartesian scope: (&CWV * [o9EKG Customer].[Project Name].['+project+'] * [o9EKG Table].[Table Name].['+Select[j].Table.Value+'] * [o9EKGSequence].[o9EKGSequenceNumber].['+Select[j].Seq.Value+'] );Measure.[o9EKG Ignored Validation Error Text] = null ;Measure.[o9EKG Ignore Validation Type] = null;Measure.[o9EKG Ignore Severity] = null;end scope;';
